        url: "legalaid.vic.gov.au",
        description: "Get free or low-cost legal help...",
        isAd: false
      },
      {
        type: "good", 
        title: "Smith & Partners Law - Collingwood Legal Experts",
        url: "smithpartnerslaw.com.au",
