    @apply border-border;
  }

  body {
    @apply bg-white text-foreground font-body;
  }

  h1,
  h2,
  h3,
  h4,
  h5,
  h6 {
    @apply font-heading;
    font-weight: 600;
    letter-spacing: -0.02em;
