import React, { useEffect, useMemo, useRef, useState, useLayoutEffect } from 'react';

interface AnimatedAdjectiveProps {
  className?: string;
}

export const AnimatedAdjective = React.memo(function AnimatedAdjective({ className = "" }: AnimatedAdjectiveProps) {
  const adjectives = useMemo(() => [
    { word: 'refined', style: 'tracking-wide' },
    { word: 'precise', style: 'tracking-normal' },
    { word: 'elegant', style: 'tracking-wide' },
    { word: 'focused', style: 'tracking-normal' },
    { word: 'polished', style: 'tracking-normal' },
    { word: 'strategic', style: 'tracking-tight' },
    { word: 'crafted', style: 'tracking-normal' },
    { word: 'premium', style: 'tracking-wide' },
    { word: 'tailored', style: 'tracking-normal' },
    { word: 'artful', style: 'tracking-wide' }
  ], []);

  const [currentIndex, setCurrentIndex] = useState(0);
  const [isVisible, setIsVisible] = useState(true);
  const [calculatedWidth, setCalculatedWidth] = useState<number | null>(null);
  const [isMounted, setIsMounted] = useState(false);
  const measureRef = useRef<HTMLSpanElement>(null);
  const hiddenMeasureRef = useRef<HTMLSpanElement>(null);

  // Ensure component is mounted (client-side only)
  useEffect(() => {
    setIsMounted(true);
  }, []);

  // Calculate width for current word (only on client)
  useLayoutEffect(() => {
    if (isMounted && hiddenMeasureRef.current) {
      const width = hiddenMeasureRef.current.getBoundingClientRect().width;
      setCalculatedWidth(width);
    }
  }, [currentIndex, className, adjectives, isMounted]);



  useEffect(() => {
    if (!isMounted) return;

    const interval = setInterval(() => {
      // Start fade out
      setIsVisible(false);

      setTimeout(() => {
        // Change to next word
        setCurrentIndex((prevIndex) =>
          prevIndex === adjectives.length - 1 ? 0 : prevIndex + 1
        );

        setTimeout(() => {
          setIsVisible(true);
        }, 100); // Small delay to ensure width is calculated
      }, 250); // Quick transition

    }, 4000); // Clean 4-second intervals

    return () => clearInterval(interval);
  }, [adjectives.length, isMounted]);

  // Render static version during SSR, animated version after mount
  if (!isMounted) {
    return (
      <span className={`${adjectives[0]?.style || ''} ${className}`}>
        {adjectives[0]?.word || 'refined'}
      </span>
    );
  }

  return (
    <span className="relative inline-block">
      {/* Hidden measurement element */}
      <span 
        ref={hiddenMeasureRef}
        className={`absolute opacity-0 pointer-events-none whitespace-nowrap ${className}`}
        aria-hidden="true"
        style={{ top: '-9999px' }}
      >
        {adjectives[currentIndex]?.word || ''}
      </span>

      {/* Space reservation container with proper baseline alignment */}
      <span 
        className="inline-block transition-all duration-150 ease-out relative align-baseline"
        style={{ 
          width: calculatedWidth && calculatedWidth > 0 ? `${Math.ceil(calculatedWidth)}px` : 'auto',
          minWidth: calculatedWidth && calculatedWidth > 0 ? `${Math.ceil(calculatedWidth)}px` : 'auto',
          // Remove fixed height to allow natural text flow and baseline alignment
          verticalAlign: 'baseline' // Ensure proper vertical alignment with surrounding text
        }}
      >
        {/* Actual animated text with proper baseline positioning */}
        <span
          ref={measureRef}
          className={`absolute left-0 whitespace-nowrap transition-all duration-400 ${isVisible ? 'opacity-100 translate-y-0 scale-100' : 'opacity-0 translate-y-1 scale-95'} ${adjectives[currentIndex]?.style || ''} ${className}`}
          style={{
            transitionTimingFunction: 'cubic-bezier(0.4, 0, 0.2, 1)',
            textShadow: isVisible ? '0 1px 3px rgba(45, 90, 135, 0.12)' : 'none',
            letterSpacing: isVisible ? '0.01em' : '0em',
            fontWeight: isVisible ? '500' : '400',
            // Use baseline alignment instead of top positioning
            bottom: '0', // Align to baseline instead of top
            transform: isVisible ? 'translateY(0) scale(1)' : 'translateY(0.125em) scale(0.95)', // Reduce transform on desktop
            // Ensure text scales properly with parent font size
            fontSize: 'inherit',
            lineHeight: 'inherit'
          }}
        >
          {adjectives[currentIndex]?.word || ''}
        </span>
      </span>
    </span>
  );
});

import { useTranslation } from 'next-i18next';
import { useRouter } from 'next/router';
import { useState, useRef, useEffect } from 'react';
import { ChevronDown, Globe } from 'lucide-react';

export function LanguageSwitcher() {
  const { t, i18n } = useTranslation('common');
  const router = useRouter();
  const [isOpen, setIsOpen] = useState(false);
  const dropdownRef = useRef<HTMLDivElement>(null);

  const languages = [
    { code: 'en', name: t('languages.en'), flag: '🇺🇸' },
    { code: 'id', name: t('languages.id'), flag: '🇮🇩' },
  ];

  const currentLanguage = languages.find(lang => lang.code === i18n.language) || languages[0];

  const handleLanguageChange = (languageCode: string) => {
    const { pathname, asPath, query } = router;
    router.push({ pathname, query }, asPath, { locale: languageCode });
    setIsOpen(false);
  };

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target as Node)) {
        setIsOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  return (
    <div className="relative" ref={dropdownRef}>
      <button
        onClick={() => setIsOpen(!isOpen)}
        className="flex items-center gap-2 px-3 py-2 text-sm font-medium text-muted-foreground hover:text-primary transition-colors duration-200 rounded-md hover:bg-muted/50"
        aria-label="Change language"
        aria-expanded={isOpen}
        aria-haspopup="listbox"
      >
        <Globe className="w-4 h-4" />
        <span className="hidden sm:inline">{currentLanguage.name}</span>
        <span className="sm:hidden">{currentLanguage.code.toUpperCase()}</span>
        <ChevronDown className={`w-4 h-4 transition-transform duration-200 ${isOpen ? 'rotate-180' : ''}`} />
      </button>

      {isOpen && (
        <div 
          className="absolute right-0 mt-2 w-40 bg-white border border-border/50 rounded-lg shadow-lg z-50 py-1"
          role="listbox"
          aria-label="Language options"
        >
          {languages.map((language) => (
            <button
              key={language.code}
              onClick={() => handleLanguageChange(language.code)}
              className={`w-full px-4 py-2 text-left text-sm hover:bg-muted/50 transition-colors duration-200 flex items-center gap-3 ${
                language.code === i18n.language ? 'bg-muted/30 text-primary font-medium' : 'text-muted-foreground'
              }`}
              role="option"
              aria-selected={language.code === i18n.language}
            >
              <span className="text-base">{language.flag}</span>
              <span>{language.name}</span>
              {language.code === i18n.language && (
                <div className="ml-auto w-2 h-2 bg-accent rounded-full"></div>
              )}
            </button>
          ))}
        </div>
      )}
    </div>
  );
}

import React, { useRef, useMemo, useState, useEffect } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import * as THREE from 'three';

interface HeroThreeBackgroundProps {
  className?: string;
}

// Enterprise Constellation - Three-Pillar Node System
function EnterpriseConstellation() {
  const groupRef = useRef<THREE.Group>(null);
  const nodesRef = useRef<THREE.Group[]>([]);
  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  const connectionsRef = useRef<any[]>([]);
  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  const particlesRef = useRef<any[]>([]);
  const [motionAllowed, setMotionAllowed] = useState(true);
  const [businessContext, setBusinessContext] = useState({
    presentation_mode: false,
    corporate_network: false,
    mobile_executive: false
  });

  useEffect(() => {
    // Check for reduced motion preference
    const mediaQuery = window.matchMedia('(prefers-reduced-motion: reduce)');
    setMotionAllowed(!mediaQuery.matches);

    const handleChange = () => setMotionAllowed(!mediaQuery.matches);
    mediaQuery.addListener(handleChange);

    // Business context detection
    const detectBusinessContext = () => {
      // Detect corporate network (slower connections)
      const connection = (navigator as Navigator & { connection?: { effectiveType?: string; downlink?: number } }).connection;
      const isCorporateNetwork = connection && (
        connection.effectiveType === '3g' || (connection.downlink && connection.downlink < 2.0)
      );

      // Detect mobile executive usage (tablets, high-res mobile)
      const isMobileExecutive = /iPad|iPhone|iPod/.test(navigator.userAgent) && 
                               window.screen.width >= 768;

      setBusinessContext({
        presentation_mode: false, // Can be detected via screen sharing API
        corporate_network: !!isCorporateNetwork,
        mobile_executive: isMobileExecutive
      });
    };

    detectBusinessContext();

    return () => mediaQuery.removeListener(handleChange);
  }, []);

  // Three pillar node configurations - representing the service pillars
  const pillarNodes = useMemo(() => [
    {
      name: 'Digital Assets',
      position: new THREE.Vector3(-1.8, 1.2, -2),
      color: '#2D5A87',
      emissive: '#1B365D',
      pattern: 'precision', // Geometric precision pattern
      scale: 1.0
    },
    {
      name: 'Market Authority', 
      position: new THREE.Vector3(1.8, 1.2, -2),
      color: '#234669',
      emissive: '#2D5A87', 
      pattern: 'authority', // Expanding influence rings
      scale: 1.1
    },
    {
      name: 'Enterprise Security',
      position: new THREE.Vector3(0, -1.5, -2),
      color: '#1F2937',
      emissive: '#234669',
      pattern: 'security', // Protective barriers
      scale: 1.05
    }
  ], []);

  // Data flow connections between pillars
  const connections = useMemo(() => {
    const lines = [];
    // Connect each pillar to the others (triangle formation)
    for (let i = 0; i < pillarNodes.length; i++) {
      for (let j = i + 1; j < pillarNodes.length; j++) {
        lines.push({
          start: pillarNodes[i].position,
          end: pillarNodes[j].position,
          opacity: 0.15,
          pulseSpeed: 0.3 + Math.random() * 0.4
        });
      }
    }
    return lines;
  }, [pillarNodes]);

  // Create sophisticated node geometries
  const createNodeGeometry = (pattern: string) => {
    switch (pattern) {
      case 'precision':
        // Geometric precision - icosahedron for mathematical elegance
        return <icosahedronGeometry args={[0.15, 1]} />;
      case 'authority':
        // Authority - sphere with subtle faceting
        return <sphereGeometry args={[0.18, 16, 12]} />;
      case 'security':
        // Security - octahedron for protective symbolism
        return <octahedronGeometry args={[0.16]} />;
      default:
        return <sphereGeometry args={[0.15, 12, 8]} />;
    }
  };

  // Animation frame logic
  useFrame((state) => {
    if (!motionAllowed || !groupRef.current) return;

    const time = state.clock.elapsedTime;
    const adaptiveSpeed = businessContext.mobile_executive ? 0.7 : 1.0; // Slower for battery

    // Gentle orbital motion for entire constellation
    groupRef.current.position.y = Math.sin(time * 0.2 * adaptiveSpeed) * 0.1;
    groupRef.current.rotation.y = time * 0.05 * adaptiveSpeed;

    // Individual node behaviors based on their pillar characteristics
    nodesRef.current.forEach((nodeGroup, index) => {
      if (!nodeGroup) return;

      const config = pillarNodes[index];
      const phase = index * (Math.PI * 2 / 3); // 120-degree phase offset

      switch (config.pattern) {
        case 'precision':
          // Precise, measured rotation - mathematical consistency
          nodeGroup.rotation.x = time * 0.3 * adaptiveSpeed;
          nodeGroup.rotation.y = time * 0.2 * adaptiveSpeed;
          nodeGroup.scale.setScalar(1.0 + Math.sin(time + phase) * 0.02);
          break;

        case 'authority':
          // Building presence - subtle expansion and contraction
          const authorityScale = 1.0 + Math.sin(time * 0.4 + phase) * 0.04;
          nodeGroup.scale.setScalar(authorityScale);
          nodeGroup.rotation.z = time * 0.15 * adaptiveSpeed;
          break;

        case 'security':
          // Protective stability - slow, steady rotation
          nodeGroup.rotation.x = time * 0.1 * adaptiveSpeed;
          nodeGroup.rotation.z = time * 0.08 * adaptiveSpeed;
          break;
      }
    });

    // Animate connection pulse effects
    connectionsRef.current.forEach((connection, index) => {
      if (!connection) return;

      const material = connection.material as THREE.LineBasicMaterial;
      const pulseSpeed = connections[index]?.pulseSpeed || 0.5;
      const pulse = Math.sin(time * pulseSpeed + index) * 0.5 + 0.5;
      material.opacity = connections[index].opacity + pulse * 0.1;
    });

    // Animate data particles flowing between nodes
    particlesRef.current.forEach((particles, index) => {
      if (!particles) return;

      const material = particles.material as THREE.PointsMaterial;
      const flow = (time * 0.5 + index) % 1;
      material.opacity = 0.3 + Math.sin(flow * Math.PI * 2) * 0.2;
    });
  });

  return (
    <group ref={groupRef}>
      {/* Three Pillar Nodes */}
      {pillarNodes.map((config, index) => (
        <group
          key={`node-${index}`}
          ref={(ref) => { if (ref) nodesRef.current[index] = ref; }}
          position={config.position}
        >
          {/* Main node */}
          <mesh>
            {createNodeGeometry(config.pattern)}
            <meshStandardMaterial
              color={config.color}
              emissive={config.emissive}
              emissiveIntensity={0.2}
              roughness={0.2}
              metalness={0.4}
              transparent
              opacity={0.8}
            />
          </mesh>

          {/* Pillar-specific effect rings */}
          {config.pattern === 'authority' && (
            <>
              <mesh>
                <ringGeometry args={[0.3, 0.32, 16]} />
                <meshBasicMaterial
                  color={config.emissive}
                  transparent
                  opacity={0.1}
                />
              </mesh>
              <mesh rotation={[0, 0, Math.PI / 4]}>
                <ringGeometry args={[0.4, 0.42, 16]} />
                <meshBasicMaterial
                  color={config.color}
                  transparent
                  opacity={0.05}
                />
              </mesh>
            </>
          )}

          {config.pattern === 'security' && (
            <mesh>
              <ringGeometry args={[0.25, 0.27, 8]} />
              <meshBasicMaterial
                color={config.emissive}
                transparent
                opacity={0.15}
              />
            </mesh>
          )}
        </group>
      ))}

      {/* Data Flow Connections */}
      {connections.map((connection, index) => (
        <line
          key={`connection-${index}`}
          ref={(ref) => { if (ref) connectionsRef.current[index] = ref; }}
        >
          <bufferGeometry>
            <bufferAttribute
              attach="attributes-position"
              count={2}
              array={new Float32Array([
                connection.start.x, connection.start.y, connection.start.z,
                connection.end.x, connection.end.y, connection.end.z
              ])}
              itemSize={3}
            />
          </bufferGeometry>
          <lineBasicMaterial
            color="#2D5A87"
            transparent
            opacity={connection.opacity}
          />
        </line>
      ))}

      {/* Data Flow Particles */}
      {connections.map((connection, index) => {
        const particleCount = businessContext.corporate_network ? 3 : 6;
        const positions = new Float32Array(particleCount * 3);

        for (let i = 0; i < particleCount; i++) {
          const t = i / (particleCount - 1);
          positions[i * 3] = connection.start.x + (connection.end.x - connection.start.x) * t;
          positions[i * 3 + 1] = connection.start.y + (connection.end.y - connection.start.y) * t;
          positions[i * 3 + 2] = connection.start.z + (connection.end.z - connection.start.z) * t;
        }

        return (
          <points
            key={`particles-${index}`}
            ref={(ref) => { if (ref) particlesRef.current[index] = ref; }}
          >
            <bufferGeometry>
              <bufferAttribute
                attach="attributes-position"
                count={particleCount}
                array={positions}
                itemSize={3}
              />
            </bufferGeometry>
            <pointsMaterial
              color="#2D5A87"
              size={0.02}
              transparent
              opacity={0.4}
            />
          </points>
        );
      })}
    </group>
  );
}

function Scene() {
  return (
    <>
      {/* Professional lighting setup for enterprise sophistication */}
      <ambientLight intensity={0.3} color="#F8FAFC" />

      {/* Key light for definition */}
      <directionalLight
        position={[5, 8, 3]}
        intensity={0.6}
        color="#2D5A87"
        castShadow={false}
      />

      {/* Fill light to soften shadows */}
      <directionalLight
        position={[-3, 5, -2]}
        intensity={0.2}
        color="#234669"
      />

      {/* Rim light for premium edge definition */}
      <directionalLight
        position={[-2, -3, 5]}
        intensity={0.3}
        color="#1B365D"
      />

      <EnterpriseConstellation />
    </>
  );
}

