    }
  }

  /* Form UX enhancements */
  .form-submitting {
    pointer-events: none;
    opacity: 0.7;
    position: relative;
  }

  .form-submitting::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: rgba(255, 255, 255, 0.8);
    backdrop-filter: blur(2px);
