      <div className="container mx-auto max-w-7xl relative z-20">
        <div className="grid lg:grid-cols-2 gap-16 lg:gap-24 items-center min-h-[calc(100vh-8rem)]">

          {/* Left Content */}
          <div className="text-center lg:text-left">
            <header className="mb-12">
              <h1
                ref={headlineAnimation.ref}
                id="hero-heading"
                className={`text-5xl sm:text-6xl lg:text-7xl xl:text-8xl font-heading font-light leading-tight text-gallifrey-charcoal tracking-tight animate-fade-up hero-animation ${
                  headlineAnimation.isVisible ? "visible complete" : ""
                }`}
              >
                {t('hero.title')}{' '}
                <span className="text-authority bg-gradient-to-r from-blue-600 to-blue-800 bg-clip-text text-transparent">
                  {t('hero.titleHighlight')}
                </span>
              </h1>

              <p className="text-xl sm:text-2xl lg:text-2xl text-gallifrey-charcoal/60 font-light mt-6 leading-relaxed max-w-xl mx-auto lg:mx-0">
                {t('hero.subtitle')}
              </p>
            </header>

            <div
              ref={ctaAnimation.ref}
              className={`flex flex-col items-center lg:items-start animate-fade-up animate-delay-600 ${
                ctaAnimation.isVisible ? "visible" : ""
              }`}
            >
              <Button
                size="lg"
                variant="gallifrey"
                className="px-10 py-4 text-lg font-semibold tracking-wide group bg-gallifrey-charcoal hover:bg-gallifrey-charcoal/90 text-white border-0 shadow-md hover:shadow-lg hover:scale-[1.02] transition-all duration-300"
                asChild
              >
                <a
                  href="#contact"
                  onClick={() => handleCTAClick("hero-secure-assets")}
                >
                  {t('hero.cta')}
                  <ArrowRight className="w-5 h-5 ml-3 transition-transform group-hover:translate-x-1" />
                </a>
              </Button>

              {/* Enhanced Security Trust Indicators */}
              <div className="mt-8 text-center lg:text-left">
                <p className="text-xs text-gallifrey-charcoal/40 mb-3">{t('hero.trustIndicators.title')}</p>
                <div className="flex flex-wrap items-center justify-center lg:justify-start gap-3">
                  <div className="flex items-center gap-2 px-3 py-2 bg-gallifrey-teal/10 border border-gallifrey-teal/20 rounded-lg hover:bg-gallifrey-teal/15 transition-colors">
                    <Shield className="w-4 h-4 text-gallifrey-teal" />
                    <span className="text-xs font-medium text-gallifrey-teal">{t('hero.trustIndicators.iso')}</span>
                  </div>
                  <div className="flex items-center gap-2 px-3 py-2 bg-gallifrey-teal/10 border border-gallifrey-teal/20 rounded-lg hover:bg-gallifrey-teal/15 transition-colors">
                    <Award className="w-4 h-4 text-gallifrey-teal" />
                    <span className="text-xs font-medium text-gallifrey-teal">{t('hero.trustIndicators.soc')}</span>
                  </div>
                  <div className="flex items-center gap-2 px-3 py-2 bg-gallifrey-teal/10 border border-gallifrey-teal/20 rounded-lg hover:bg-gallifrey-teal/15 transition-colors">
                    <CheckCircle className="w-4 h-4 text-gallifrey-teal" />
                    <span className="text-xs font-medium text-gallifrey-teal">{t('hero.trustIndicators.gdpr')}</span>
                  </div>
                </div>
              </div>

              {/* Security Metrics Dashboard */}
              <div className="mt-6 bg-gallifrey-teal/5 border border-gallifrey-teal/20 rounded-xl p-6">
                <div className="grid grid-cols-1 sm:grid-cols-3 gap-6 text-center">
                  <div>
                    <div className="text-2xl sm:text-3xl font-bold text-gallifrey-teal mb-1">99.99%</div>
                    <div className="text-xs text-gallifrey-charcoal/60">Security Uptime</div>
                  </div>
                  <div>
                    <div className="text-2xl sm:text-3xl font-bold text-gallifrey-teal mb-1">0</div>
                    <div className="text-xs text-gallifrey-charcoal/60">Security Breaches</div>
                  </div>
                  <div>
                    <div className="text-2xl sm:text-3xl font-bold text-gallifrey-teal mb-1">70%</div>
                    <div className="text-xs text-gallifrey-charcoal/60">Cost Reduction</div>
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* Right Content - Enhanced Coin */}
          <div className="flex justify-center lg:justify-end">
            <CoinAnimation 
              size="large"
              autoFlip={true} 
              flipInterval={4000} 
              scrollTrigger={true}
            />
          </div>
        </div>
      </div>
    </section>
  );
});

