  }

  /* Security Animations */

  @keyframes security-pulse {
    0% { 
      transform: scale(1);
      filter: drop-shadow(0 0 4px currentColor);
    }
    50% { 
      transform: scale(1.1);
      filter: drop-shadow(0 0 12px currentColor);
    }
    100% { 
      transform: scale(1);
      filter: drop-shadow(0 0 4px currentColor);
    }
  }

  @keyframes network-connect {
    0% { 
      stroke-dashoffset: 100;
      opacity: 0.3;
    }
    50% { 
      opacity: 0.8;
    }
    100% { 
      stroke-dashoffset: 0;
      opacity: 0.3;
    }
  }


  .security-pulse {
