                      <Award className="w-6 h-6 text-gallifrey-teal mr-2" />
                      <div className="text-3xl sm:text-4xl font-bold text-gallifrey-teal">70%</div>
                    </div>
                    <div className="text-sm font-medium text-gallifrey-charcoal/70">{t('hero.metrics.savings')}</div>
