          {/* Right Content - Enhanced Coin */}
          <div className="flex justify-center lg:justify-end">
            <CoinAnimation 
              size="large"
              autoFlip={true} 
              flipInterval={4000} 
              scrollTrigger={true}
            />
          </div>
        </div>
      </div>
    </section>
  );
});

