              className="px-8 py-3 border-muted-foreground/20 hover:bg-muted/50"
              asChild
            >
              <a href="/case-studies">
                {t('cta.secondaryButton')}
              </a>
            </Button>
          </div>
        </div>
      </div>
    </section>
  );
}

