            </div>

            {/* Trust Indicators - Horizontal Layout */}
            <div className="flex flex-wrap items-center justify-center gap-6">
              {trustIndicators.map((indicator, index) => {
                const Icon = indicator.icon;
                return (
                  <div 
                    key={indicator.key}
                    className={`flex items-center gap-2 px-3 py-2 bg-white/20 border border-white/30 rounded-lg backdrop-blur-sm hover:bg-white/30 transition-all duration-300 transform hover:scale-105 ${
                      ctaAnimation.isVisible 
