                  animationDelay={1000}
                  className="text-left"
                />
              </div>
            </div>

            {/* Right: Enhanced Metrics */}
            <div 
              ref={metricsAnimation.ref}
              className={`bg-gradient-to-br from-white/10 via-white/5 to-transparent border border-white/20 rounded-2xl p-8 backdrop-blur-sm transform transition-all duration-700 ease-out delay-800 ${
                metricsAnimation.isVisible 
                  ? "opacity-100 translate-y-0 scale-100" 
                  : "opacity-0 translate-y-12 scale-95"
              }`}
            >
              <div className="space-y-6">
                <div className="text-center mb-6">
                  <h3 className="text-lg font-semibold text-gray-800 mb-2">Infrastructure Performance</h3>
                  <p className="text-sm text-gray-600">Real-time enterprise metrics</p>
                </div>

                <div className="space-y-4">
                  <div className="flex justify-between items-center group hover:scale-105 transition-transform duration-300">
                    <div className="flex items-center gap-3">
                      <div className="p-2 bg-workos-blue-500/10 rounded-lg">
                        <Shield className="w-5 h-5 text-workos-blue-600" />
                      </div>
                      <span className="text-sm font-medium text-gray-700">Uptime</span>
                    </div>
                    <GradientText
                      colors={{
                        from: 'from-workos-blue-500',
                        to: 'to-workos-teal-500'
                      }}
                      className="text-2xl font-black"
                    >
                      99.99%
                    </GradientText>
                  </div>

                  <div className="flex justify-between items-center group hover:scale-105 transition-transform duration-300">
                    <div className="flex items-center gap-3">
                      <div className="p-2 bg-workos-teal-500/10 rounded-lg">
                        <CheckCircle className="w-5 h-5 text-workos-teal-600" />
                      </div>
                      <span className="text-sm font-medium text-gray-700">Threats Blocked</span>
                    </div>
                    <GradientText
                      colors={{
                        from: 'from-workos-teal-500',
                        to: 'to-workos-blue-500'
                      }}
                      className="text-2xl font-black"
                    >
                      847
                    </GradientText>
                  </div>

                  <div className="flex justify-between items-center group hover:scale-105 transition-transform duration-300">
                    <div className="flex items-center gap-3">
                      <div className="p-2 bg-workos-purple-500/10 rounded-lg">
                        <Award className="w-5 h-5 text-workos-purple-600" />
                      </div>
                      <span className="text-sm font-medium text-gray-700">Cost Savings</span>
                    </div>
                    <GradientText
                      colors={{
                        from: 'from-workos-purple-500',
