import React, { Suspense, useCallback } from "react";
import { ArrowRight } from "lucide-react";
import { Button } from "./ui/button";
import { trackCTAClick } from "@/lib/analytics";
import { useScrollAnimation } from "@/lib/useScrollAnimation";
import { PlaceholderAnimation } from "./PlaceholderAnimation";
import { PerformanceProfiler } from "./PerformanceProfiler";
import dynamic from "next/dynamic";
import { useTranslation } from 'next-i18next';

// Use Security background instead of Gallifreyan
const SecurityBackground = dynamic(
  () => import("./SecurityBackground").then(mod => ({ default: mod.SecurityBackground })),
  { 
    ssr: false, 
    loading: () => <PlaceholderAnimation />
  }
);

// Enhanced coin animation component
const CoinAnimation = dynamic(
  () => import("./CoinAnimation").then(mod => ({ default: mod.CoinAnimation })),
  { 
    ssr: false, 
    loading: () => <div className="w-[180px] h-[180px] bg-gray-200 rounded-full animate-pulse" />
  }
);

export const Hero = React.memo(function Hero() {
  const { t } = useTranslation('home');
  const headlineAnimation = useScrollAnimation<HTMLHeadingElement>({
    threshold: 0.1,
  });
  const ctaAnimation = useScrollAnimation<HTMLDivElement>({ threshold: 0.1 });

  const handleCTAClick = useCallback((action: string) => {
    trackCTAClick(action);
  }, []);

  return (
    <section
      className="relative min-h-screen px-4 overflow-hidden flex items-center"
      style={{ minHeight: 'calc(100vh - 4rem)' }}
      aria-labelledby="hero-heading"
      data-testid="hero-section-security"
    >
      <PerformanceProfiler id="SecurityBackground">
        <Suspense fallback={<PlaceholderAnimation />}>
          <SecurityBackground className="opacity-70" />
        </Suspense>
      </PerformanceProfiler>

      <div className="container mx-auto max-w-7xl relative z-20">
        <div className="grid lg:grid-cols-2 gap-16 lg:gap-24 items-center min-h-[calc(100vh-8rem)]">

          {/* Left Content */}
          <div className="text-center lg:text-left">
            <header className="mb-12">
              <h1
                ref={headlineAnimation.ref}
                id="hero-heading"
                className={`text-5xl sm:text-6xl lg:text-7xl xl:text-8xl font-heading font-light leading-tight text-gallifrey-charcoal tracking-tight animate-fade-up hero-animation ${
                  headlineAnimation.isVisible ? "visible complete" : ""
                }`}
              >
                {t('hero.title')}{' '}
                <span className="text-authority bg-gradient-to-r from-blue-600 to-blue-800 bg-clip-text text-transparent">
                  {t('hero.titleHighlight')}
                </span>
              </h1>

              <p className="text-xl sm:text-2xl lg:text-2xl text-gallifrey-charcoal/60 font-light mt-6 leading-relaxed max-w-xl mx-auto lg:mx-0">
                {t('hero.subtitle')}
              </p>
            </header>

            <div
              ref={ctaAnimation.ref}
              className={`flex flex-col items-center lg:items-start animate-fade-up animate-delay-600 ${
                ctaAnimation.isVisible ? "visible" : ""
              }`}
            >
              <Button
                size="lg"
                variant="gallifrey"
                className="px-10 py-4 text-lg font-semibold tracking-wide group bg-gallifrey-charcoal hover:bg-gallifrey-charcoal/90 text-white border-0 shadow-md hover:shadow-lg hover:scale-[1.02] transition-all duration-300"
                asChild
              >
                <a
                  href="#contact"
                  onClick={() => handleCTAClick("hero-secure-assets")}
                >
                  {t('hero.cta')}
                  <ArrowRight className="w-5 h-5 ml-3 transition-transform group-hover:translate-x-1" />
                </a>
              </Button>

              {/* Trust Indicators */}
              <div className="mt-6 text-center lg:text-left">
                <p className="text-xs text-gallifrey-charcoal/40 mb-2">{t('hero.trustIndicators.title')}</p>
                <div className="flex items-center justify-center lg:justify-start gap-3 text-xs text-gallifrey-charcoal/50">
                  <span>{t('hero.trustIndicators.iso')}</span>
                  <span>•</span>
                  <span>{t('hero.trustIndicators.soc')}</span>
                  <span>•</span>
                  <span>{t('hero.trustIndicators.gdpr')}</span>
                </div>
              </div>
            </div>
          </div>

          {/* Right Content - Enhanced Coin */}
          <div className="flex justify-center lg:justify-end">
            <CoinAnimation 
              size="large"
              autoFlip={true} 
              flipInterval={4000} 
              scrollTrigger={true}
            />
          </div>
        </div>
      </div>
    </section>
  );
});

import { Code, Shield, Target } from "lucide-react";
import { useScrollAnimation, useStaggeredAnimation } from "@/lib/useScrollAnimation";

export function Services() {
  const headerAnimation = useScrollAnimation<HTMLDivElement>();
  const { ref: servicesRef, visibleItems } = useStaggeredAnimation<HTMLDivElement>(3);

  const services = [
    {
      icon: <Code className="w-6 h-6 text-accent" aria-hidden="true" />,
      title: "Bespoke Web Development",
      description: "Custom-built websites and web applications designed specifically for your business needs.",
      details: [
        "Custom React/Next.js applications",
        "Progressive Web Apps (PWA)",
        "E-commerce solutions",
        "API development and integration"
      ]
    },
    {
      icon: <Target className="w-6 h-6 text-accent" aria-hidden="true" />,
      title: "Performance Optimization",
      description: "Lightning-fast websites that convert visitors into customers through superior user experience.",
      details: [
        "Core Web Vitals optimization",
        "SEO performance improvements",
        "Mobile-first responsive design",
        "Advanced caching strategies"
      ]
    },
    {
      icon: <Shield className="w-6 h-6 text-accent" aria-hidden="true" />,
      title: "Security & Compliance",
      description: "Enterprise-grade security measures and compliance frameworks to protect your digital assets.",
      details: [
        "GDPR & CCPA compliance",
        "ISO 27001 security standards",
        "SOC 2 Type II compliance",
        "Advanced threat protection"
      ]
    }
  ];

  return (
    <section id="services" className="py-16 px-4" aria-labelledby="services-heading" role="main">
      <div className="container mx-auto max-w-6xl">
        {/* Section header */}
        <div
          ref={headerAnimation.ref}
          className={`mb-20 animate-fade-up ${headerAnimation.isVisible ? 'visible' : ''}`}
        >
          <div className="mb-8">
            <p className="text-sm font-medium tracking-[0.1em] text-muted-foreground uppercase mb-4">
              Services
            </p>
            <div className="w-12 h-px bg-accent"></div>
          </div>

          <h2 id="services-heading" className="text-3xl md:text-5xl font-heading font-medium leading-tight mb-6 text-primary max-w-4xl tracking-tight">
            Security-first web development
            <span className="italic text-accent"> that protects your business</span>
          </h2>

          <p className="text-lg text-muted-foreground max-w-3xl leading-relaxed">
            From custom web applications to enterprise security compliance, we deliver comprehensive digital solutions that safeguard your business while driving growth.
          </p>
        </div>

        {/* Services grid */}
        <div ref={servicesRef} className="grid lg:grid-cols-3 gap-6 md:gap-8 mb-20">
          {services.map((service, index) => (
            <div
              key={index}
              className={`group animate-fade-up ${visibleItems[index] ? 'visible' : ''}`}
            >
              <div className="flex items-start gap-4 mb-6">
                <div className="w-12 h-12 bg-accent/10 rounded-full flex items-center justify-center group-hover:bg-accent/20 transition-colors">
                  {service.icon}
                </div>
                <div className="flex-1">
                  <h3 className="font-heading text-xl font-medium mb-3 text-primary tracking-tight">
                    {service.title}
                  </h3>
                  <p className="text-muted-foreground leading-relaxed mb-4">
                    {service.description}
                  </p>
                  <div className="space-y-2">
                    {service.details.map((detail, detailIndex) => (
                      <div key={detailIndex} className="flex items-center gap-2 text-sm text-muted-foreground/80">
                        <div className="w-1 h-1 bg-accent rounded-full"></div>
                        <span>{detail}</span>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>

      </div>
    </section>
  );
}

{
