          title: 'Gallifrey Consulting - Security-First Web Development Melbourne',
          description: 'Melbourne-based web development agency specializing in secure, GDPR-compliant websites. Starting at $1000. Own your digital narrative with custom web solutions.',
          images: [
            {
              url: 'https://gallifrey.consulting/og-image.jpg',
              width: 1200,
              height: 630,
              alt: 'Gallifrey Consulting - Security-First Web Development',
            },
          ],
        }}
        twitter={{
          handle: '@gallifreyconsulting',
          site: '@gallifreyconsulting',
          cardType: 'summary_large_image',
        }}
        additionalMetaTags={[
          {
            name: 'viewport',
            content: 'width=device-width, initial-scale=1',
          },
          {
            name: 'robots',
            content: 'index, follow, max-image-preview:large, max-snippet:-1, max-video-preview:-1',
          },
          {
            name: 'author',
            content: 'Gallifrey Consulting',
          },
          {
            name: 'geo.region',
            content: 'AU-VIC',
          },
          {
            name: 'geo.placename',
            content: 'Melbourne',
          },
          {
            name: 'geo.position',
            content: '-37.8136;144.9631',
          },
          {
            name: 'ICBM',
            content: '-37.8136, 144.9631',
          },
          {
            name: 'theme-color',
            content: '#1B365D',
          },
          {
            name: 'msapplication-TileColor',
            content: '#1B365D',
          },
          {
            name: 'msapplication-config',
            content: '/browserconfig.xml',
          },
        ]}
        additionalLinkTags={[
          {
            rel: 'icon',
            href: '/favicon.ico',
          },
          {
            rel: 'icon',
            type: 'image/png',
            sizes: '32x32',
            href: '/favicon-32x32.png',
          },
          {
            rel: 'icon',
            type: 'image/png',
            sizes: '16x16',
            href: '/favicon-16x16.png',
          },
          {
            rel: 'apple-touch-icon',
            href: '/apple-touch-icon.png',
            sizes: '180x180',
          },
          {
            rel: 'manifest',
            href: '/site.webmanifest',
          },
          {
            rel: 'preconnect',
            href: 'https://fonts.googleapis.com',
          },
          {
            rel: 'preconnect',
            href: 'https://fonts.gstatic.com',
            crossOrigin: 'anonymous',
          },
        ]}
      />
      <ABTestProvider>
        <LayoutABTestProvider>
          <Component {...pageProps} />
