file.content:
import { Menu, X, Sparkles } from "lucide-react";
import Link from "next/link";

import { Button } from "./ui/button";
import { Logo } from "./Logo";
import { LanguageSwitcher } from "./LanguageSwitcher";
import { useState, useEffect } from "react";
import { useTranslation } from 'next-i18next';

export function Header() {
  const { t } = useTranslation('common');
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 20);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const navItems = [
    { href: "#services", label: t('navigation.services') },
    { href: "#testimonials", label: t('navigation.results') },
    { href: "#pricing", label: t('navigation.investment') },
    { href: "#faq", label: t('navigation.faq') },
    { href: "#contact", label: t('navigation.contact') },
  ];

  return (
    <>
      {/* Skip to main content link for accessibility */}
      <a 
        href="#services" 
        className="sr-only focus:not-sr-only focus:absolute focus:top-4 focus:left-4 focus:z-[100] focus:px-4 focus:py-2 focus:bg-primary focus:text-white focus:rounded"
      >
        {t('navigation.skipToMain')}
      </a>
      <header className={`border-b border-border/30 backdrop-blur supports-[backdrop-filter]:bg-white/60 sticky top-0 z-50 transition-all duration-300 ${
      isScrolled 
        ? 'bg-white/98 shadow-sm py-2' 
        : 'bg-white/95 py-0'
    }`}>
      <div className="container mx-auto px-4">
        <div className={`flex items-center justify-between transition-all duration-300 ${
          isScrolled ? 'h-14' : 'h-16'
        }`}>
          {/* Logo */}
          <div className="flex items-center">
            <Logo width={120} height={37} />
          </div>

          {/* Desktop Navigation */}
          <nav className="hidden lg:flex items-center space-x-8" role="navigation" aria-label="Main navigation">
            {navItems.map((item) => (
              <a
                key={item.href}
                href={item.href}
                className="text-sm font-medium text-muted-foreground hover:text-primary transition-colors duration-200"
              >
                {item.label}
              </a>
            ))}
          </nav>

          {/* CTA Button & Mobile Menu */}
          <div className="flex items-center space-x-4">
            {/* Language Switcher */}
            <LanguageSwitcher />

            {/* Narrative Link */}
            <Link
              href="https://narrative.gallifrey.consulting"
              className="hidden md:flex items-center gap-1.5 text-sm font-medium text-oyn-orange-600 hover:text-oyn-orange-700 transition-colors duration-200"
            >
              <Sparkles className="w-4 h-4" />
              <span>{t('navigation.narrative')}</span>
            </Link>

            <Button
              size="sm"
              className="hidden sm:flex text-sm bg-primary hover:bg-primary/90 text-primary-foreground px-6"
              asChild
            >
              <a href="https://calendly.com/rickithadi/30min" target="_blank" rel="noopener noreferrer">
                {t('navigation.getStarted')}
              </a>
            </Button>

            {/* Mobile menu button */}
            <button
              className="lg:hidden p-2 hover:bg-muted/50 rounded-md transition-colors duration-200"
              onClick={() => setIsMenuOpen(!isMenuOpen)}
              aria-label="Toggle menu"
            >
              {isMenuOpen ? (
                <X className="w-5 h-5 text-primary" />
              ) : (
                <Menu className="w-5 h-5 text-primary" />
              )}
            </button>
          </div>
        </div>

        {/* Mobile Navigation */}
        {isMenuOpen && (
          <div className="lg:hidden py-6 border-t border-border/30">
            <nav className="flex flex-col space-y-4" role="navigation" aria-label="Mobile navigation">
              {navItems.map((item) => (
                <a
                  key={item.href}
                  href={item.href}
                  className="text-sm font-medium text-muted-foreground hover:text-primary transition-colors duration-200 py-2"
                  onClick={() => setIsMenuOpen(false)}
                >
                  {item.label}
                </a>
              ))}
              <Link
                href="https://narrative.gallifrey.consulting"
                className="text-sm font-medium text-oyn-orange-600 hover:text-oyn-orange-700 transition-colors duration-200 py-2 flex items-center gap-1.5"
                onClick={() => setIsMenuOpen(false)}
              >
                <Sparkles className="w-4 h-4" />
                <span>{t('navigation.narrative')}</span>
              </Link>
              <div className="pt-4 border-t border-border/30">
                <Button
                  variant="outline"
                  size="sm"
                  className="w-full text-sm border-muted-foreground/20"
                  asChild
                >
                  <a href="https://calendly.com/rickithadi/30min" target="_blank" rel="noopener noreferrer">
                    {t('navigation.getStarted')}
                  </a>
                </Button>
              </div>
            </nav>
          </div>
        )}
      </div>
    </header>
    </>
  );
}

{
  "navigation": {
    "services": "Services",
    "results": "Results", 
    "investment": "Investment",
    "faq": "FAQ",
    "contact": "Contact",
    "getStarted": "Get Started",
    "narrative": "Narrative",
    "skipToMain": "Skip to main content"
  },
  "languages": {
    "en": "English",
    "id": "Indonesian"
  },
  "buttons": {
    "learnMore": "Learn More",
    "getStarted": "Get Started",
    "contact": "Contact Us",
    "sendMessage": "Send us a message",
    "bookConsultation": "Book consultation call",
    "scheduleCall": "Schedule Discovery Call",
    "viewStories": "View Success Stories"
  },
  "footer": {
    "copyright": "© 2024 Gallifrey Consulting. All rights reserved.",
    "privacy": "Privacy Policy",
    "terms": "Terms of Service"
  },
  "seo": {
    "defaultTitle": "Global AI Security | Enterprise Digital Protection",
    "defaultDescription": "World's premier AI security agency. AI-resistant development, deepfake protection, prompt injection prevention, quantum-secure digital solutions with enterprise AI governance."
  }
}

