    >
      <Sparkles className="w-4 h-4" />
      <span>{t('navigation.narrative')}</span>
    </Link>

    <div className="pt-4 border-t border-border/30 space-y-3">
      <Button
        className="w-full text-sm bg-primary hover:bg-primary/90 text-primary-foreground py-3 font-medium shadow-sm hover:shadow-md transition-all duration-200 focus:ring-2 focus:ring-primary/50 focus:ring-offset-2"
        asChild
      >
        <a href="https://calendly.com/rickithadi/30min" target="_blank" rel="noopener noreferrer" onClick={closeMenu}>
          <Shield className="w-4 h-4 mr-2" />
          {t('navigation.getStarted')}
        </a>
      </Button>
    </div>
  </nav>
</div>

