tool_response.originalFileContents:
import { Menu, X, Sparkles, Shield } from "lucide-react";
import Link from "next/link";

import { Button } from "./ui/button";
import { Logo } from "./Logo";
import { LanguageSwitcher } from "./LanguageSwitcher";
import { useState, useEffect, useCallback, memo } from "react";
import { useTranslation } from 'next-i18next';

const HeaderComponent = function Header() {
  const { t } = useTranslation('common');
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);
  const [activeSection, setActiveSection] = useState('');

  const handleScroll = useCallback(() => {
    const scrollPosition = window.scrollY;
    setIsScrolled(scrollPosition > 20);

    // Update active section for better UX
    const sections = ['services', 'testimonials', 'pricing', 'faq', 'contact'];
    const currentSection = sections.find(section => {
      const element = document.getElementById(section);
      if (element) {
        const rect = element.getBoundingClientRect();
        return rect.top <= 100 && rect.bottom >= 100;
      }
      return false;
    });
    setActiveSection(currentSection || '');
  }, []);

  useEffect(() => {
    window.addEventListener('scroll', handleScroll, { passive: true });
    return () => window.removeEventListener('scroll', handleScroll);
  }, [handleScroll]);

  const closeMenu = useCallback(() => {
    setIsMenuOpen(false);
  }, []);

  const handleNavClick = useCallback((href: string) => {
    closeMenu();
    // Smooth scroll to section
    const element = document.querySelector(href);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth', block: 'start' });
    }
  }, [closeMenu]);

  const navItems = [
    { href: "#services", label: t('navigation.services'), id: 'services' },
    { href: "#testimonials", label: t('navigation.results'), id: 'testimonials' },
    { href: "#pricing", label: t('navigation.investment'), id: 'pricing' },
    { href: "#faq", label: t('navigation.faq'), id: 'faq' },
    { href: "#contact", label: t('navigation.contact'), id: 'contact' },
  ];

  return (
    <>
      {/* Skip to main content link for accessibility */}
      <a 
        href="#services" 
        className="sr-only focus:not-sr-only focus:absolute focus:top-4 focus:left-4 focus:z-[100] focus:px-6 focus:py-3 focus:bg-primary focus:text-white focus:rounded-md focus:font-medium focus:shadow-lg focus:ring-2 focus:ring-primary/50 focus:ring-offset-2 transition-all"
        onFocus={(e) => e.target.scrollIntoView({ behavior: 'smooth', block: 'nearest' })}
      >
        {t('navigation.skipToMain')}
      </a>
      <header className={`border-b border-border/30 backdrop-blur-md supports-[backdrop-filter]:bg-white/80 sticky top-0 z-50 transition-all duration-300 ${
      isScrolled 
        ? 'bg-white/98 shadow-lg shadow-primary/5 py-2' 
        : 'bg-white/95 py-0'
    }`}
      role="banner"
      aria-label="Site header"
    >
      <div className="container mx-auto px-4">
        <div className={`flex items-center justify-between transition-all duration-300 ${
          isScrolled ? 'h-14' : 'h-18'
        }`}>
          {/* Logo */}
          <div className="flex items-center group">
            <Link href="/" className="flex items-center gap-3 focus:outline-none focus:ring-2 focus:ring-primary/50 focus:ring-offset-2 rounded-md transition-all">
              <div className="relative">
                <Logo width={isScrolled ? 110 : 130} height={isScrolled ? 34 : 40} className="transition-all duration-300" />
                <div className="absolute -inset-1 bg-gradient-to-r from-primary/20 to-accent/20 rounded-lg opacity-0 group-hover:opacity-100 transition-opacity duration-300 -z-10" />
              </div>
            </Link>
          </div>

          {/* Desktop Navigation */}
          <nav className="hidden lg:flex items-center space-x-1" role="navigation" aria-label="Main navigation">
            {navItems.map((item) => {
              const isActive = activeSection === item.id;
              return (
                <a
                  key={item.href}
                  href={item.href}
                  onClick={(e) => {
                    e.preventDefault();
                    handleNavClick(item.href);
                  }}
                  className={`relative px-4 py-2 text-sm font-medium rounded-md transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-primary/50 focus:ring-offset-2 ${
                    isActive 
                      ? 'text-primary bg-primary/10 shadow-sm' 
                      : 'text-muted-foreground hover:text-primary hover:bg-primary/5'
                  }`}
                  aria-current={isActive ? 'page' : undefined}
                >
                  {item.label}
                  {isActive && (
                    <div className="absolute bottom-0 left-1/2 transform -translate-x-1/2 w-1 h-1 bg-primary rounded-full" />
                  )}
                </a>
              );
            })}
          </nav>

          {/* CTA Button & Mobile Menu */}
          <div className="flex items-center space-x-4">
            {/* Language Switcher */}
            <div className="hidden sm:block">
              <LanguageSwitcher />
            </div>

            {/* Narrative Link */}
            <Link
              href="https://narrative.gallifrey.consulting"
              className="hidden md:flex items-center gap-2 px-3 py-2 text-sm font-medium text-oyn-orange-600 hover:text-oyn-orange-700 hover:bg-oyn-orange-50 rounded-md transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-oyn-orange-500/50 focus:ring-offset-2"
              target="_blank"
              rel="noopener noreferrer"
            >
              <Sparkles className="w-4 h-4" />
              <span>{t('navigation.narrative')}</span>
            </Link>

            <Button
              size="sm"
              className="hidden sm:flex items-center gap-2 text-sm bg-primary hover:bg-primary/90 text-primary-foreground px-6 py-2.5 font-medium shadow-sm hover:shadow-md transition-all duration-200 focus:ring-2 focus:ring-primary/50 focus:ring-offset-2"
              asChild
            >
              <a href="https://calendly.com/rickithadi/30min" target="_blank" rel="noopener noreferrer">
                <Shield className="w-4 h-4" />
                {t('navigation.getStarted')}
              </a>
            </Button>

            {/* Mobile menu button */}
            <button
              className="lg:hidden p-2.5 hover:bg-primary/10 rounded-md transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-primary/50 focus:ring-offset-2"
              onClick={() => setIsMenuOpen(!isMenuOpen)}
              aria-label={isMenuOpen ? 'Close menu' : 'Open menu'}
              aria-expanded={isMenuOpen}
              aria-controls="mobile-menu"
            >
              {isMenuOpen ? (
                <X className="w-5 h-5 text-primary" />
              ) : (
                <Menu className="w-5 h-5 text-primary" />
              )}
            </button>
          </div>
        </div>

        {/* Mobile Navigation */}
        <div 
          id="mobile-menu"
          className={`lg:hidden overflow-hidden transition-all duration-300 ease-in-out ${
            isMenuOpen 
              ? 'max-h-[500px] opacity-100 py-6 border-t border-border/30' 
              : 'max-h-0 opacity-0'
          }`}
          aria-hidden={!isMenuOpen}
        >
          <nav className="flex flex-col space-y-2" role="navigation" aria-label="Mobile navigation">
            {navItems.map((item) => {
              const isActive = activeSection === item.id;
              return (
                <a
                  key={item.href}
                  href={item.href}
                  onClick={(e) => {
                    e.preventDefault();
                    handleNavClick(item.href);
                  }}
                  className={`text-sm font-medium rounded-md px-3 py-3 transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-primary/50 focus:ring-offset-2 ${
                    isActive 
                      ? 'text-primary bg-primary/10 shadow-sm' 
                      : 'text-muted-foreground hover:text-primary hover:bg-primary/5'
                  }`}
                  aria-current={isActive ? 'page' : undefined}
                >
                  <div className="flex items-center justify-between">
                    <span>{item.label}</span>
                    {isActive && <div className="w-2 h-2 bg-primary rounded-full" />}
                  </div>
                </a>
              );
            })}

            <div className="py-2">
              <LanguageSwitcher />
            </div>

            <Link
              href="https://narrative.gallifrey.consulting"
              className="text-sm font-medium text-oyn-orange-600 hover:text-oyn-orange-700 hover:bg-oyn-orange-50 transition-all duration-200 py-3 px-3 rounded-md flex items-center gap-2 focus:outline-none focus:ring-2 focus:ring-oyn-orange-500/50 focus:ring-offset-2"
              onClick={closeMenu}
              target="_blank"
              rel="noopener noreferrer"
            >
              <Sparkles className="w-4 h-4" />
              <span>{t('navigation.narrative')}</span>
            </Link>

            <div className="pt-4 border-t border-border/30 space-y-3">
              <Button
                className="w-full text-sm bg-primary hover:bg-primary/90 text-primary-foreground py-3 font-medium shadow-sm hover:shadow-md transition-all duration-200 focus:ring-2 focus:ring-primary/50 focus:ring-offset-2"
                asChild
              >
                <a href="https://calendly.com/rickithadi/30min" target="_blank" rel="noopener noreferrer" onClick={closeMenu}>
                  <Shield className="w-4 h-4 mr-2" />
                  {t('navigation.getStarted')}
                </a>
              </Button>
            </div>
          </nav>
        </div>
      </div>
    </header>
    </>
  );
};

// Memoize the header component for performance
export const Header = memo(HeaderComponent, () => {
  // Header has no props, so we can optimize by preventing unnecessary re-renders
  return true;
});

