tool_response.originalFileContents:
@tailwind base;
@tailwind components;
@tailwind utilities;

/* Google Fonts import with optimized loading */
@import url("https://fonts.googleapis.com/css2?family=Montserrat:wght@400;500;600;700&family=Source+Sans+Pro:wght@400;500;600&family=Playfair+Display:wght@400;500;600;700&display=swap");

/* Font display optimization for performance */
@font-face {
  font-family: 'Montserrat';
  font-display: swap;
}

@font-face {
  font-family: 'Source Sans Pro';
  font-display: swap;
}

@font-face {
  font-family: 'Playfair Display';
  font-display: swap;
}

:root {
  /* ===== GALLIFREY BRAND COLORS - Production Site Corporate Palette ===== */
  --gallifrey-white: 255 255 255; /* #FFFFFF - Pure white backgrounds */
  --gallifrey-off-white: 249 250 251; /* #F9FAFB - Subtle section backgrounds */
  --gallifrey-light-gray: 96 103 112; /* #606770 - Improved contrast 4.51:1 (was #9CA3AF 2.85:1) */
  --gallifrey-medium-gray: 107 114 128; /* #6B7280 - Secondary text */
  --gallifrey-dark-gray: 55 65 81; /* #374151 - Body text */
  --gallifrey-charcoal: 31 41 55; /* #1F2937 - Primary text & headings */
  --gallifrey-teal: 28 73 113; /* #1C4971 - Deeper Steel Blue-Teal - commanding authority with sophistication */
  --gallifrey-teal-dark: 21 58 91; /* #153A5B - Deep steel blue hover state */
  --gallifrey-teal-light: 45 90 135; /* #2D5A87 - Original teal for subtle accents */
  --gallifrey-border: 229 231 235; /* #E5E7EB - Subtle borders */

  /* ===== NARRATIVE CAMPAIGN COLORS - Refined Sage & Copper Palette ===== */
  --oyn-stone-50: 252 252 251; /* #FCFCFB - Softer white */
  --oyn-stone-100: 247 246 244; /* #F7F6F4 - Warmer light gray */
  --oyn-stone-200: 232 230 227; /* #E8E6E3 - Sophisticated neutral */
  --oyn-stone-300: 210 206 201; /* #D2CEC9 - Elegant medium gray */
  --oyn-stone-400: 158 151 143; /* #9E978F - Refined contrast */
  --oyn-stone-500: 135 128 119; /* #878077 - Sage undertone */
  --oyn-stone-600: 95 88 82; /* #5F5852 - Rich brown-gray */
  --oyn-stone-700: 71 65 60; /* #47413C - Deep charcoal */
  --oyn-stone-800: 45 41 37; /* #2D2925 - Near black */
  --oyn-stone-900: 32 29 26; /* #201D1A - Sophisticated black */
  --oyn-orange-50: 254 252 247; /* #FEFCF7 - Cream whisper */
  --oyn-orange-100: 252 244 232; /* #FCF4E8 - Warm cream */
  --oyn-orange-200: 247 229 204; /* #F7E5CC - Soft bronze */
  --oyn-orange-300: 237 204 166; /* #EDCCA6 - Muted gold */
  --oyn-orange-400: 218 165 106; /* #DAA56A - Refined copper */
  --oyn-orange-500: 197 139 85; /* #C58B55 - Elegant bronze */
  --oyn-orange-600: 168 113 68; /* #A87144 - Deep copper */
  --oyn-orange-700: 139 92 56; /* #8B5C38 - Rich bronze */
  --oyn-orange-800: 110 73 45; /* #6E492D - Dark copper */
  --oyn-orange-900: 85 57 36; /* #553924 - Deepest bronze */

  /* ===== SEMANTIC COLOR MAPPINGS - Gallifrey Brand Default ===== */
  --background: var(--gallifrey-white);
  --foreground: var(--gallifrey-charcoal);
  --card: var(--gallifrey-white);
  --card-foreground: var(--gallifrey-charcoal);
  --popover: var(--gallifrey-white);
  --popover-foreground: var(--gallifrey-charcoal);
  --primary: var(--gallifrey-teal);
  --primary-foreground: var(--gallifrey-white);
  --secondary: var(--gallifrey-off-white);
  --secondary-foreground: var(--gallifrey-charcoal);
  --muted: var(--gallifrey-off-white);
  --muted-foreground: var(--gallifrey-medium-gray);
  --accent: var(--gallifrey-teal);
  --accent-foreground: var(--gallifrey-white);
  --destructive: 239 68 68; /* red-500 equivalent */
  --destructive-foreground: var(--gallifrey-white);
  --border: var(--gallifrey-border);
  --input: var(--gallifrey-white);
  --ring: var(--gallifrey-teal);
  --chart-1: var(--gallifrey-teal);
  --chart-2: var(--gallifrey-charcoal);
  --chart-3: var(--gallifrey-teal-dark);
  --chart-4: var(--gallifrey-off-white);
  --chart-5: var(--gallifrey-medium-gray);
  --radius: 0.375rem; /* 6px for friendlier feel */
}

.dark {
  --background: 222 84% 5%;
  --foreground: 210 11% 96%;
  --card: 217 33% 17%;
  --card-foreground: 210 11% 96%;
  --popover: 217 33% 17%;
  --popover-foreground: 210 11% 96%;
  --primary: 174 64% 47%;
  --primary-foreground: 222 84% 5%;
  --secondary: 215 28% 17%;
  --secondary-foreground: 210 11% 96%;
  --muted: 215 28% 17%;
  --muted-foreground: 217 11% 65%;
  --accent: 22 100% 54%;
  --accent-foreground: 222 84% 5%;
  --destructive: 0 63% 31%;
  --destructive-foreground: 210 11% 96%;
  --border: 215 28% 17%;
  --input: 215 28% 17%;
  --ring: 174 64% 47%;
  --chart-1: 174 64% 47%;
  --chart-2: 22 100% 54%;
  --chart-3: 210 11% 96%;
  --chart-4: 217 11% 65%;
  --chart-5: 215 16% 57%;
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply bg-white text-foreground font-body;
  }

  h1 {
    @apply font-heading;
    font-weight: 700;
    letter-spacing: -0.02em;
  }

  h2 {
    @apply font-heading;
    font-weight: 650;
    letter-spacing: -0.015em;
  }

  h3,
  h4,
  h5,
  h6 {
    @apply font-heading;
    font-weight: 600;
    letter-spacing: -0.02em;
  }
}

@layer utilities {
  /* ===== GALLIFREY BRAND UTILITIES ===== */
  .text-gradient-gallifrey {
    background: linear-gradient(
      135deg,
      rgb(var(--gallifrey-charcoal)),
      rgb(var(--gallifrey-teal))
    );
    background-clip: text;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
  }

  .text-command {
    font-weight: 700;
    letter-spacing: -0.025em;
    color: rgb(var(--gallifrey-charcoal));
  }

  .text-authority {
    font-weight: 650;
    letter-spacing: -0.015em;
    color: rgb(var(--gallifrey-teal));
  }

  .shadow-command {
    box-shadow: 0 8px 32px rgba(var(--gallifrey-teal), 0.15);
  }

  .text-gradient-gallifrey-elegant {
    background: linear-gradient(
      135deg,
      rgb(var(--gallifrey-teal)),
      rgb(var(--gallifrey-charcoal))
    );
    background-clip: text;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
  }

  .shadow-gallifrey {
    box-shadow: 0 4px 6px -1px rgb(var(--gallifrey-charcoal) / 0.1),
      0 2px 4px -1px rgb(var(--gallifrey-charcoal) / 0.06);
  }

  .shadow-gallifrey-lg {
    box-shadow: 0 10px 15px -3px rgb(var(--gallifrey-charcoal) / 0.1),
      0 4px 6px -2px rgb(var(--gallifrey-charcoal) / 0.05);
  }

  .shadow-gallifrey-xl {
    box-shadow: 0 20px 25px -5px rgb(var(--gallifrey-charcoal) / 0.1),
      0 10px 10px -5px rgb(var(--gallifrey-charcoal) / 0.04);
  }

  .bg-gallifrey-section {
    background-color: rgb(var(--gallifrey-off-white));
  }

  .border-gallifrey {
    border-color: rgb(var(--gallifrey-border));
  }

  /* ===== OWN YOUR NARRATIVE CAMPAIGN UTILITIES ===== */
  .text-gradient-oyn {
    background: linear-gradient(
      135deg,
      rgb(var(--oyn-stone-800)),
      rgb(var(--oyn-orange-600))
    );
    background-clip: text;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
  }

  .bg-oyn-gradient {
    background: linear-gradient(
      135deg,
      rgb(var(--oyn-stone-100)),
      rgb(var(--oyn-stone-50))
    );
  }

  .bg-oyn-warm {
    background-color: rgb(var(--oyn-stone-50));
  }

  .shadow-oyn {
    box-shadow: 0 4px 6px -1px rgb(var(--oyn-stone-800) / 0.1),
      0 2px 4px -1px rgb(var(--oyn-stone-800) / 0.06);
  }

  .shadow-oyn-lg {
    box-shadow: 0 10px 15px -3px rgb(var(--oyn-stone-800) / 0.1),
      0 4px 6px -2px rgb(var(--oyn-stone-800) / 0.05);
  }

  /* ===== HERO ANIMATIONS ===== */
  @keyframes float {
    0%, 100% { transform: translateY(0px); }
    50% { transform: translateY(-20px); }
  }

  @keyframes float-delayed {
    0%, 100% { transform: translateY(0px); }
    50% { transform: translateY(-15px); }
  }

  @keyframes shimmer {
    0% { transform: translate(-50%, -50%) scaleX(0); }
    50% { transform: translate(-50%, -50%) scaleX(1); }
    100% { transform: translate(-50%, -50%) scaleX(0); }
  }

  @keyframes fadeIn {
    from { 
      opacity: 0; 
      transform: translateY(10px); 
    }
    to { 
      opacity: 1; 
      transform: translateY(0); 
    }
  }

  @keyframes cursor-blink {
    0%, 50% { opacity: 1; }
    51%, 100% { opacity: 0; }
  }

  /* ===== SECURITY ANIMATIONS ===== */

  @keyframes security-pulse {
    0%, 100% { 
      opacity: 0.4; 
      transform: translate(-50%, -50%) scale(1); 
    }
    50% { 
      opacity: 0.8; 
      transform: translate(-50%, -50%) scale(1.1); 
    }
  }

  @keyframes network-connect {
    0% { 
      stroke-dashoffset: 20; 
      opacity: 0.2; 
    }
    100% { 
      stroke-dashoffset: 0; 
      opacity: 0.6; 
    }
  }


  .animate-security-pulse {
    animation: security-pulse 3s ease-in-out infinite;
  }

  .animate-network-connect {
    stroke-dasharray: 20;
    animation: network-connect 0.8s ease-out forwards;
  }

  @keyframes slide-in-left {
    0% { transform: translateX(-100px); opacity: 0; }
    100% { transform: translateX(0); opacity: 1; }
  }

  @keyframes slide-in-right {
    0% { transform: translateX(100px); opacity: 0; }
    100% { transform: translateX(0); opacity: 1; }
  }

  @keyframes slide-in-bottom {
    0% { transform: translateY(100px); opacity: 0; }
    100% { transform: translateY(0); opacity: 1; }
  }

  @keyframes bounce-in {
    0% { transform: scale(0.8); opacity: 0; }
    50% { transform: scale(1.05); opacity: 0.8; }
    100% { transform: scale(1); opacity: 1; }
  }

  .animate-float {
    animation: float 6s ease-in-out infinite;
  }

  .animate-float-delayed {
    animation: float-delayed 8s ease-in-out infinite 2s;
  }

  .animate-shimmer {
    animation: shimmer 4s ease-in-out infinite;
  }

  .animate-slide-in-left {
    animation: slide-in-left 0.8s ease-out forwards;
  }

  .animate-slide-in-right {
    animation: slide-in-right 0.8s ease-out forwards;
  }

  .animate-slide-in-bottom {
    animation: slide-in-bottom 0.5s ease-out forwards;
  }

  .animate-bounce-in {
    animation: bounce-in 0.6s cubic-bezier(0.68, -0.55, 0.265, 1.55) forwards;
  }

  .animate-fadeIn {
    animation: fadeIn 0.5s ease-out forwards;
  }

  .animate-cursor-blink {
    animation: cursor-blink 1s infinite;
  }

  /* ===== SHARED TYPOGRAPHY UTILITIES ===== */
  .logo-serif {
    font-family: "Playfair Display", serif;
    font-weight: 600;
    letter-spacing: 0.02em;
  }

  .elegant-caps {
    font-variant: small-caps;
    letter-spacing: 0.15em;
    font-weight: 500;
  }

  /* ===== LEGACY UTILITIES (for backward compatibility) ===== */
  .text-gradient-brand {
    @apply text-gradient-gallifrey;
  }

  .text-gradient-elegant {
    @apply text-gradient-gallifrey-elegant;
  }

  .shadow-brand {
    @apply shadow-gallifrey;
  }

  .shadow-brand-lg {
    @apply shadow-gallifrey-lg;
  }

  .shadow-brand-xl {
    @apply shadow-gallifrey-xl;
  }

  /* Own Your Narrative Campaign Styles - Keep existing for OYN section */
  .narrative-gradient {
    background: linear-gradient(135deg, #1e40af 0%, #0d9488 100%);
  }

  .narrative-text-gradient {
    @apply bg-gradient-to-r from-blue-600 via-teal-500 to-blue-600 bg-clip-text text-transparent;
  }

  .narrative-glow {
    box-shadow: 0 4px 20px rgba(59, 130, 246, 0.15);
  }

  .narrative-border {
    border-image: linear-gradient(135deg, #3b82f6, #14b8a6) 1;
  }

  /* Apple-inspired glassmorphism */
  .glass-card {
    background: rgba(255, 255, 255, 0.8);
    backdrop-filter: blur(20px);
    border: 1px solid rgba(255, 255, 255, 0.2);
  }

  .glass-card-dark {
    background: rgba(15, 23, 42, 0.8);
    backdrop-filter: blur(20px);
    border: 1px solid rgba(255, 255, 255, 0.1);
  }

  /* Smooth transitions for Apple-like feel */
  .smooth-transition {
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  /* Custom scrollbar for clean Apple aesthetic */
  ::-webkit-scrollbar {
    width: 8px;
  }

  ::-webkit-scrollbar-track {
    background: transparent;
  }

  ::-webkit-scrollbar-thumb {
    background: rgba(148, 163, 184, 0.3);
    border-radius: 4px;
  }

  ::-webkit-scrollbar-thumb:hover {
    background: rgba(148, 163, 184, 0.5);
  }

  /* Scroll Animation Classes */
  .animate-fade-up {
    opacity: 0;
    transform: translateY(20px);
    transition: opacity 600ms cubic-bezier(0.4, 0, 0.2, 1),
      transform 600ms cubic-bezier(0.4, 0, 0.2, 1);
  }

  .animate-fade-up.visible {
    opacity: 1;
    transform: translateY(0);
  }

  .animate-scale-in {
    opacity: 0;
    transform: scale(0.95);
    transition: opacity 500ms cubic-bezier(0.34, 1.56, 0.64, 1),
      transform 500ms cubic-bezier(0.34, 1.56, 0.64, 1);
  }

  .animate-scale-in.visible {
    opacity: 1;
    transform: scale(1);
  }

  .animate-slide-left {
    opacity: 0;
    transform: translateX(30px);
    transition: opacity 600ms cubic-bezier(0.4, 0, 0.2, 1),
      transform 600ms cubic-bezier(0.4, 0, 0.2, 1);
  }

  .animate-slide-left.visible {
    opacity: 1;
    transform: translateX(0);
  }

  /* Stagger delays */
  .animate-delay-100 {
    transition-delay: 100ms;
  }

  .animate-delay-200 {
    transition-delay: 200ms;
  }

  .animate-delay-300 {
    transition-delay: 300ms;
  }

  .animate-delay-400 {
    transition-delay: 400ms;
  }

  .animate-delay-500 {
    transition-delay: 500ms;
  }

  .animate-delay-600 {
    transition-delay: 600ms;
  }

  /* Performance optimizations with will-change */
  .animate-fade-up,
  .animate-scale-in,
  .animate-slide-left,
  .animate-fade-in-up,
  .animate-float,
  .animate-shimmer,
  .animate-bounce-in {
    will-change: transform, opacity;
  }

  .animate-fade-up.visible,
  .animate-scale-in.visible,
  .animate-slide-left.visible,
  .animate-fade-in-up.visible {
    will-change: auto;
  }

  /* Hero specific animation optimizations */
  .hero-animation {
    will-change: transform, opacity;
    contain: layout style paint;
  }

  .hero-animation.complete {
    will-change: auto;
  }

  /* Respect reduced motion preference - Comprehensive coverage */
  @media (prefers-reduced-motion: reduce) {
    .animate-fade-up,
    .animate-scale-in,
    .animate-slide-left,
    .animate-slide-in-left,
    .animate-slide-in-right,
    .animate-slide-in-bottom,
    .animate-bounce-in,
    .animate-float,
    .animate-float-delayed,
    .animate-shimmer,
    .animate-security-pulse,
    .animate-network-connect,
    .animate-fadeIn,
    .animate-cursor-blink,
    .hero-animation,
    .security-pulse {
      animation: none !important;
      transition: none !important;
      opacity: 1 !important;
      transform: none !important;
    }

    /* Ensure visibility for elements that might be hidden by animations */
    .animate-fade-up,
    .animate-scale-in,
    .animate-slide-left,
    .animate-fadeIn {
      opacity: 1;
      transform: none;
    }

    /* Google search animation - show final state immediately */
    .google-search-animation * {
      animation: none !important;
      transition: none !important;
    }
  }

  /* Enhanced mobile touch targets */
  @media (max-width: 768px) {
    .mobile-touch-target,
    .touch-target-optimized {
      min-height: 48px;
      min-width: 48px;
      padding: 12px 24px;
      display: inline-flex;
      align-items: center;
      justify-content: center;
    }

    /* Larger mobile buttons */
    .btn-mobile {
      padding: 16px 32px;
      font-size: 1.1rem;
      min-height: 52px;
    }

    /* Better mobile card spacing */
    .mobile-card:hover {
      transform: none; /* Disable hover transforms on mobile */
    }

    /* Improved mobile navigation */
    .mobile-nav {
      padding: 16px;
    }

    /* Better mobile form controls */
    .mobile-input {
      min-height: 48px;
      font-size: 16px; /* Prevents zoom on iOS */
    }
  }

  /* Security Animations */

  @keyframes security-pulse {
    0% { 
      transform: scale(1);
      filter: drop-shadow(0 0 4px currentColor);
    }
    50% { 
      transform: scale(1.1);
      filter: drop-shadow(0 0 12px currentColor);
    }
    100% { 
      transform: scale(1);
      filter: drop-shadow(0 0 4px currentColor);
    }
  }

  @keyframes network-connect {
    0% { 
      stroke-dashoffset: 100;
      opacity: 0.3;
    }
    50% { 
      opacity: 0.8;
    }
    100% { 
      stroke-dashoffset: 0;
      opacity: 0.3;
    }
  }


  .security-pulse {
    animation: security-pulse 0.6s ease-out;
  }

  .security-node:hover {
    animation: network-connect 1s ease-in-out;
    stroke-dasharray: 100;
  }

  /* Enhanced hover states for desktop */
  @media (hover: hover) {
    .hover-lift:hover {
      transform: translateY(-4px);
      box-shadow: 0 12px 40px rgba(var(--gallifrey-charcoal), 0.15);
      transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    }

    .hover-scale:hover {
      transform: scale(1.02);
      transition: transform 0.2s ease-out;
    }

    .hover-glow:hover {
      box-shadow: 0 0 20px rgba(var(--gallifrey-teal), 0.3);
      transition: box-shadow 0.3s ease-out;
    }
  }

  /* Form UX enhancements */
  .form-submitting {
    pointer-events: none;
    opacity: 0.7;
    position: relative;
  }

  .form-submitting::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: rgba(255, 255, 255, 0.8);
    backdrop-filter: blur(2px);
    z-index: 10;
  }

  /* Loading spinner animation */
  @keyframes spin {
    to { transform: rotate(360deg); }
  }

  .animate-spin {
    animation: spin 1s linear infinite;
  }

  /* Success state animations */
  @keyframes checkmark {
    0% { stroke-dashoffset: 100; }
    100% { stroke-dashoffset: 0; }
  }

  .animate-checkmark {
    stroke-dasharray: 100;
    animation: checkmark 0.6s ease-in-out forwards;
  }

  /* Campaign page performance optimizations */
  .campaign-gradient {
    contain: layout style paint;
    will-change: transform;
  }

  /* ===== COIN ANIMATION STYLES ===== */
  .coin-container {
    perspective: 1000px;
    display: flex;
    flex-direction: column;
    align-items: center;
    cursor: pointer;
  }

  .coin {
    width: 120px;
    height: 120px;
    position: relative;
    transform-style: preserve-3d;
    transition: transform 1.2s cubic-bezier(0.175, 0.885, 0.32, 1.275);
    animation: coinFloat 6s ease-in-out infinite;
  }

  .coin.flipped {
    transform: rotateY(180deg);
    animation: coinFloatFlipped 6s ease-in-out infinite;
  }

  .coin-face {
    position: absolute;
    width: 100%;
    height: 100%;
    border-radius: 50%;
    backface-visibility: hidden;
    display: flex;
    align-items: center;
    justify-content: center;
    background: linear-gradient(135deg, rgb(var(--gallifrey-teal)), rgb(var(--gallifrey-teal-dark)));
    box-shadow: 
      0 8px 32px rgba(var(--gallifrey-teal), 0.3),
      inset 0 2px 4px rgba(255, 255, 255, 0.2);
    border: 2px solid rgba(255, 255, 255, 0.2);
  }

  .coin-front {
    transform: rotateY(0deg);
  }

  .coin-back {
    transform: rotateY(180deg);
  }

  .coin-content {
    display: flex;
    flex-direction: column;
    align-items: center;
    text-align: center;
  }

  .coin:hover {
    transform: translateY(-4px) rotateY(0deg);
    transition: all 0.3s ease-out;
  }

  .coin.flipped:hover {
    transform: translateY(-4px) rotateY(180deg);
  }

  /* Float animation keyframes */
  @keyframes coinFloat {
    0%, 100% {
      transform: translateY(0px);
    }
    50% {
      transform: translateY(-8px);
    }
  }


  @keyframes coinFloatFlipped {
    0%, 100% {
      transform: translateY(0px) rotateY(180deg);
    }
    50% {
      transform: translateY(-8px) rotateY(180deg);
    }
  }

  @media (prefers-reduced-motion: reduce) {
    .coin {
      transition: none;
      animation: none;
    }

    .coin:hover {
      transform: none;
    }

    .coin.flipped {
      animation: none;
    }
  }

  .campaign-cta {
    min-height: 48px;
    padding: 16px 24px;
  }

  @media (max-width: 768px) {
    .campaign-cta {
      min-height: 52px;
      padding: 18px 28px;
      font-size: 1.1rem;
    }
  }

  /* Accessibility enhancements */
  .sr-only {
    position: absolute;
    width: 1px;
    height: 1px;
    padding: 0;
    margin: -1px;
    overflow: hidden;
    clip: rect(0, 0, 0, 0);
    white-space: nowrap;
    border: 0;
  }

  .focus\:not-sr-only:focus {
    position: static;
    width: auto;
    height: auto;
    padding: inherit;
    margin: inherit;
    overflow: visible;
    clip: auto;
    white-space: normal;
  }

  /* High contrast mode support */
  @media (prefers-contrast: high) {
    .btn {
      border: 2px solid currentColor;
    }

    .card {
      border: 2px solid currentColor;
    }
  }

  /* Reduced data mode optimizations */
  @media (prefers-reduced-data: reduce) {
    .animate-fade-up,
    .animate-scale-in,
    .animate-slide-left,
    .animate-float,
    .animate-shimmer {
      animation: none;
      transition: none;
    }

    .glass-card,
    .glass-card-dark {
      backdrop-filter: none;
      background: rgba(255, 255, 255, 0.95);
    }
  }

  /* ===== ENHANCED NAVBAR STYLES ===== */

  /* Professional navbar with security-first styling */
  .navbar-enhanced {
    background: rgba(255, 255, 255, 0.95);
    backdrop-filter: blur(12px);
    border-bottom: 1px solid rgba(var(--gallifrey-border), 0.3);
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .navbar-enhanced.scrolled {
    background: rgba(255, 255, 255, 0.98);
    box-shadow: 0 4px 20px rgba(var(--gallifrey-charcoal), 0.08);
    backdrop-filter: blur(16px);
  }

  /* Enhanced navigation items - Ultra-minimal executive styling */
  .nav-item-enhanced {
    position: relative;
    padding: 8px 16px; /* 8px grid system */
    font-weight: 400;
    font-size: 0.875rem;
    letter-spacing: 0.02em;
    transition: color 0.2s ease-out;
    color: rgb(var(--gallifrey-medium-gray));
  }

  .nav-item-enhanced:hover {
    color: rgb(var(--gallifrey-teal));
    background-color: rgba(var(--gallifrey-teal), 0.015);
  }

  .nav-item-enhanced.active {
    color: rgb(var(--gallifrey-teal));
    background-color: rgba(var(--gallifrey-teal), 0.02);
  }

  .nav-item-enhanced.active::after {
    content: '';
    position: absolute;
    bottom: -1px;
    left: 50%;
    transform: translateX(-50%);
    width: 24px; /* 8px grid: 3x8 */
    height: 1px;
    background-color: rgba(var(--gallifrey-teal), 0.3);
  }

  /* Enhanced mobile menu - Executive minimal */
  .mobile-menu-enhanced {
    background: rgba(255, 255, 255, 0.98);
    backdrop-filter: blur(8px);
    border-top: 1px solid rgba(var(--gallifrey-border), 0.2);
    border-radius: 0 0 4px 4px; /* Consistent 4px radius */
  }

  .mobile-nav-item {
    padding: 16px; /* 8px grid: 2x8 */
    margin: 4px 0; /* 8px grid: 0.5x8 */
    font-weight: 400;
    letter-spacing: 0.02em;
    transition: color 0.2s ease-out;
  }

  .mobile-nav-item:hover,
  .mobile-nav-item.active {
    background-color: rgba(var(--gallifrey-teal), 0.015);
    color: rgb(var(--gallifrey-teal));
  }

  .mobile-nav-item.active {
    background-color: rgba(var(--gallifrey-teal), 0.025);
  }

  /* Enhanced logo styling - Minimal approach */
  .logo-enhanced {
    transition: opacity 0.2s ease-out;
  }

  .logo-enhanced:hover {
    opacity: 0.95;
  }

  /* Enhanced CTA button - Executive minimal */
  .cta-enhanced {
    background: rgb(var(--gallifrey-teal));
    border-radius: 4px; /* Consistent radius */
    padding: 8px 24px; /* 8px grid */
    font-weight: 400;
    letter-spacing: 0.02em;
    transition: background-color 0.2s ease-out;
  }

  .cta-enhanced:hover {
    background: rgb(var(--gallifrey-teal-dark));
  }

  /* Security-themed visual enhancements - Ultra-minimal */
  .security-indicator {
    position: relative;
    display: inline-flex;
    align-items: center;
    gap: 8px; /* 8px grid */
  }

  .security-indicator::before {
    content: '';
    width: 4px;
    height: 4px;
    background-color: rgba(16, 185, 129, 0.6);
    border-radius: 50%;
  }

  /* Keyboard navigation enhancements - Minimal focus styles */
  .nav-item-enhanced:focus {
    outline: 1px solid rgba(var(--gallifrey-teal), 0.4);
    outline-offset: 1px;
    background-color: rgba(var(--gallifrey-teal), 0.02);
  }

  .mobile-nav-item:focus {
    outline: 1px solid rgba(var(--gallifrey-teal), 0.4);
    outline-offset: 1px;
  }

  /* Smooth mobile menu transitions */
  .mobile-menu-slide-enter {
    max-height: 0;
    opacity: 0;
    transform: translateY(-10px);
  }

  .mobile-menu-slide-enter-active {
    max-height: 500px;
    opacity: 1;
    transform: translateY(0);
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .mobile-menu-slide-exit {
    max-height: 500px;
    opacity: 1;
    transform: translateY(0);
  }

  .mobile-menu-slide-exit-active {
    max-height: 0;
    opacity: 0;
    transform: translateY(-10px);
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  /* Minimal animations - Removed pulsing for executive look */
  @keyframes navIndicatorPulse {
    /* Removed for ultra-minimal approach */
  }

  @keyframes securityStatusPulse {
    /* Removed for ultra-minimal approach */
  }

  /* ===== SECURITY-THEMED ANIMATIONS ===== */

  /* Security Coin Animations */
  .security-coin-container {
    perspective: 1200px;
    display: flex;
    flex-direction: column;
    align-items: center;
    position: relative;
    cursor: pointer;
  }

  .security-coin, .security-coin-large {
    position: relative;
    transform-style: preserve-3d;
    transition: transform 1.2s cubic-bezier(0.34, 1.56, 0.64, 1);
    animation: securityFloat 8s ease-in-out infinite;
    will-change: transform;
  }

  .security-coin {
    width: 120px;
    height: 120px;
  }

  .security-coin-large {
    width: 180px;
    height: 180px;
  }

  .security-coin.flipped, .security-coin-large.flipped {
    transform: rotateY(180deg);
  }

  .security-coin.hovered, .security-coin-large.hovered {
    transform: scale(1.05) rotateY(-15deg);
    animation-play-state: paused;
    filter: drop-shadow(0 10px 25px rgba(59, 130, 246, 0.3));
  }

  .security-coin:hover .security-coin-face,
  .security-coin-large:hover .security-coin-face {
    box-shadow: 
      0 0 40px rgba(59, 130, 246, 0.6),
      0 12px 40px rgba(59, 130, 246, 0.3),
      inset 0 0 25px rgba(255, 255, 255, 0.15),
      inset 0 2px 6px rgba(255, 255, 255, 0.4);
    border-color: rgba(255, 255, 255, 0.5);
  }

  .security-coin-face {
    position: absolute;
    width: 100%;
    height: 100%;
    border-radius: 50%;
    backface-visibility: hidden;
    display: flex;
    align-items: center;
    justify-content: center;
    background: linear-gradient(135deg, #1e3a8a 0%, #1e40af 50%, #1d4ed8 100%);
    border: 3px solid rgba(255, 255, 255, 0.3);
    box-shadow: 
      0 0 30px rgba(59, 130, 246, 0.4),
      0 8px 32px rgba(59, 130, 246, 0.2),
      inset 0 0 20px rgba(255, 255, 255, 0.1),
      inset 0 2px 4px rgba(255, 255, 255, 0.3);
    overflow: hidden;
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .security-coin-front {
    background: linear-gradient(135deg, #dc2626 0%, #ef4444 50%, #f87171 100%);
    box-shadow: 
      0 0 30px rgba(239, 68, 68, 0.4),
      inset 0 0 20px rgba(255, 255, 255, 0.1);
  }

  .security-coin-back {
    transform: rotateY(180deg);
    background: linear-gradient(135deg, #059669 0%, #10b981 50%, #34d399 100%);
    box-shadow: 
      0 0 30px rgba(16, 185, 129, 0.4),
      inset 0 0 20px rgba(255, 255, 255, 0.1);
  }

  .security-coin-content {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    text-align: center;
    z-index: 2;
    position: relative;
  }

  .security-icon-group {
    position: relative;
    display: flex;
    align-items: center;
    justify-content: center;
  }

  .security-indicators {
    display: flex;
    gap: 4px;
    margin-top: 8px;
  }

  .security-dot {
    width: 6px;
    height: 6px;
    border-radius: 50%;
    background: rgba(255, 255, 255, 0.3);
    transition: all 0.3s ease;
  }

  .security-dot.active {
    background: rgba(255, 255, 255, 0.9);
    box-shadow: 0 0 8px rgba(255, 255, 255, 0.6);
    animation: securityPulse 2s ease-in-out infinite;
  }

  .security-overlay {
    position: absolute;
    inset: 0;
    border-radius: 50%;
    opacity: 0;
    transition: opacity 0.3s ease;
  }

  .surveillance-overlay {
    background: radial-gradient(circle at center, 
      rgba(239, 68, 68, 0.2) 0%, 
      rgba(220, 38, 38, 0.1) 50%, 
      transparent 100%);
    animation: surveillanceScan 3s ease-in-out infinite;
  }

  .defense-overlay {
    background: radial-gradient(circle at center, 
      rgba(16, 185, 129, 0.2) 0%, 
      rgba(5, 150, 105, 0.1) 50%, 
      transparent 100%);
    animation: defenseShield 4s ease-in-out infinite;
  }

  .security-coin:hover .security-overlay,
  .security-coin-large:hover .security-overlay {
    opacity: 1;
  }

  /* Security Status */
  .security-status {
    margin-top: 16px;
    padding: 8px 16px;
    border-radius: 20px;
    background: rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
    border: 1px solid rgba(255, 255, 255, 0.2);
  }

  /* Scanning Ring Effect */
  .security-scan-ring {
    position: absolute;
    width: 220px;
    height: 220px;
    border: 2px solid transparent;
    border-top: 2px solid rgba(59, 130, 246, 0.6);
    border-radius: 50%;
    animation: securityScan 4s linear infinite;
    z-index: 1;
  }

  /* Particle System */
  .security-particles {
    position: absolute;
    inset: 0;
    pointer-events: none;
  }

  .security-particle {
    position: absolute;
    width: 4px;
    height: 4px;
    background: radial-gradient(circle, #60a5fa, transparent);
    border-radius: 50%;
    top: 50%;
    left: 50%;
    transform-origin: 0 0;
    animation: particleExplosion 1s ease-out forwards;
  }

  /* Security Background Elements */
  .security-grid-pattern {
    position: absolute;
    inset: 0;
    background-image: 
      linear-gradient(rgba(59, 130, 246, 0.1) 1px, transparent 1px),
      linear-gradient(90deg, rgba(59, 130, 246, 0.1) 1px, transparent 1px);
    background-size: 50px 50px;
    animation: gridDrift 20s linear infinite;
  }

  .security-hexagon-field {
    position: absolute;
    inset: 0;
  }

  .security-hexagon {
    position: absolute;
    width: 30px;
    height: 30px;
    background: rgba(59, 130, 246, 0.1);
    clip-path: polygon(30% 0%, 70% 0%, 100% 50%, 70% 100%, 30% 100%, 0% 50%);
    animation: hexagonFloat 8s ease-in-out infinite;
  }

  .security-scan-line {
    position: absolute;
    left: 0;
    right: 0;
    height: 2px;
    background: linear-gradient(90deg, 
      transparent 0%, 
      rgba(34, 197, 94, 0.8) 50%, 
      transparent 100%);
    box-shadow: 0 0 10px rgba(34, 197, 94, 0.6);
  }

  .security-particle-field {
    position: absolute;
    inset: 0;
  }

  .security-floating-particle {
    position: absolute;
    width: 3px;
    height: 3px;
    background: rgba(59, 130, 246, 0.6);
    border-radius: 50%;
    animation: floatingParticle 6s ease-in-out infinite;
  }

  .security-binary-rain {
    position: absolute;
    inset: 0;
    font-family: 'Courier New', monospace;
    overflow: hidden;
  }

  .security-binary-column {
    position: absolute;
    top: -20px;
    font-size: 12px;
    color: rgba(34, 197, 94, 0.3);
    animation: binaryFall 8s linear infinite;
  }

  .security-binary-column span {
    display: block;
    line-height: 1.2;
  }

  .security-perimeter {
    position: absolute;
    inset: 20px;
    pointer-events: none;
  }

  .security-corner {
    position: absolute;
    width: 40px;
    height: 40px;
    border: 2px solid rgba(59, 130, 246, 0.4);
  }

  .security-corner-tl {
    top: 0;
    left: 0;
    border-right: none;
    border-bottom: none;
  }

  .security-corner-tr {
    top: 0;
    right: 0;
    border-left: none;
    border-bottom: none;
  }

  .security-corner-bl {
    bottom: 0;
    left: 0;
    border-right: none;
    border-top: none;
  }

  .security-corner-br {
    bottom: 0;
    right: 0;
    border-left: none;
    border-top: none;
  }

  .security-gradient-overlay {
    position: absolute;
    inset: 0;
    background: radial-gradient(circle at center, 
      transparent 20%, 
      rgba(255, 255, 255, 0.02) 50%, 
      rgba(59, 130, 246, 0.05) 100%);
  }

  /* Keyframe Animations */
  @keyframes securityFloat {
    0%, 100% { transform: translateY(0px) rotateX(0deg); }
    25% { transform: translateY(-8px) rotateX(5deg); }
    50% { transform: translateY(-4px) rotateX(0deg); }
    75% { transform: translateY(-12px) rotateX(-5deg); }
  }

  @keyframes securityPulse {
    0%, 100% { 
      opacity: 0.9;
      transform: scale(1);
    }
    50% { 
      opacity: 1;
      transform: scale(1.2);
    }
  }

  @keyframes securityScan {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }

  @keyframes surveillanceScan {
    0%, 100% { opacity: 0; }
    50% { opacity: 0.8; }
  }

  @keyframes defenseShield {
    0%, 100% { 
      opacity: 0;
      transform: scale(1);
    }
    50% { 
      opacity: 0.6;
      transform: scale(1.1);
    }
  }

  @keyframes particleExplosion {
    0% {
      opacity: 1;
      transform: translate(0, 0) scale(1);
    }
    50% {
      opacity: 0.8;
      transform: translate(
        calc(cos(var(--angle)) * 40px), 
        calc(sin(var(--angle)) * 40px)
      ) scale(0.6);
    }
    100% {
      opacity: 0;
      transform: translate(
        calc(cos(var(--angle)) * 60px), 
        calc(sin(var(--angle)) * 60px)
      ) scale(0);
    }
  }

  @keyframes gridDrift {
    0% { transform: translate(0, 0); }
    100% { transform: translate(50px, 50px); }
  }

  @keyframes hexagonFloat {
    0%, 100% { 
      transform: translateY(0px) rotate(0deg);
      opacity: 0.1;
    }
    50% { 
      transform: translateY(-20px) rotate(180deg);
      opacity: 0.3;
    }
  }

  @keyframes floatingParticle {
    0%, 100% { 
      transform: translateY(0px);
      opacity: 0.6;
    }
    50% { 
      transform: translateY(-30px);
      opacity: 1;
    }
  }

  @keyframes binaryFall {
    0% { 
      transform: translateY(-100vh);
      opacity: 0;
    }
    10%, 90% { opacity: 1; }
    100% { 
      transform: translateY(100vh);
      opacity: 0;
    }
  }

  /* Responsive adjustments for security elements */
  @media (max-width: 768px) {
    .security-coin-large {
      width: 140px;
      height: 140px;
    }

    .security-scan-ring {
      width: 180px;
      height: 180px;
    }

    .security-hexagon {
      width: 20px;
      height: 20px;
    }

    .security-corner {
      width: 30px;
      height: 30px;
    }
  }

  /* ===== THREE-WORD COIN FACE POSITIONING ===== */

  /* Position each face in 3D space around the coin */
  .security-face-0 {
    transform: rotateY(0deg) translateZ(90px);
  }

  .security-face-1 {
    transform: rotateY(120deg) translateZ(90px);
  }

  .security-face-2 {
    transform: rotateY(240deg) translateZ(90px);
  }

  /* Container rotation states for each face */
  .face-0 {
    transform: rotateY(0deg);
    transition: transform 1.2s cubic-bezier(0.34, 1.56, 0.64, 1);
  }

  .face-1 {
    transform: rotateY(-120deg);
    transition: transform 1.2s cubic-bezier(0.34, 1.56, 0.64, 1);
  }

  .face-2 {
    transform: rotateY(-240deg);
    transition: transform 1.2s cubic-bezier(0.34, 1.56, 0.64, 1);
  }

  /* Enhanced 3D container for proper face rendering */
  .security-coin-container {
    perspective: 1200px;
    perspective-origin: center center;
  }

  .security-coin, .security-coin-large {
    transform-style: preserve-3d;
    backface-visibility: hidden;
  }

  .security-coin-face {
    backface-visibility: hidden;
  }

  /* Reduced motion preferences for security animations */
  @media (prefers-reduced-motion: reduce) {
    .security-coin,
    .security-coin-large,
    .security-scan-ring,
    .security-hexagon,
    .security-floating-particle,
    .security-binary-column,
    .security-particle {
      animation: none !important;
    }

    .security-overlay {
      animation: none !important;
      opacity: 0.3 !important;
    }

    .security-grid-pattern {
      animation: none !important;
    }

    /* Ensure static positioning for reduced motion */
    .security-face-0,
    .security-face-1,
    .security-face-2 {
      transform: none !important;
    }

    .face-0,
    .face-1,
    .face-2 {
      transform: none !important;
    }
  }
}

