  --accent-foreground: 222 84% 5%;
  --destructive: 0 63% 31%;
  --destructive-foreground: 210 11% 96%;
  --border: 215 28% 17%;
  --input: 215 28% 17%;
  --ring: 174 64% 47%;
  --chart-1: 174 64% 47%;
  --chart-2: 22 100% 54%;
  --chart-3: 210 11% 96%;
