          <div className="flex items-center justify-center gap-8 text-xs text-gallifrey-charcoal/50">
            <span>Zero incidents since 2019</span>
            <span>•</span>
            <span>GDPR/CCPA compliant</span>
            <span>•</span>
            <span>Platform-independent solutions</span>
          </div>
        </div>
      </div>
    </section>
  );
}

