      console.log('   3. Use as template for your own documents');

    } catch (error) {
      console.error(`❌ Example generation failed: ${error.message}`);
      process.exit(1);
    }
  });

// Help command enhancement
program.on('--help', () => {
  console.log('');
  console.log('🕉️ Gallifrey Consulting Document System');
  console.log('');
  console.log('Examples:');
