                >
                  <div className="flex items-center justify-between">
                    <span className="text-gallifrey-charcoal group-hover:text-gallifrey-charcoal font-medium">
                      {option.text}
                    </span>
                    <ArrowRight className="w-4 h-4 text-gallifrey-charcoal/40 group-hover:text-gallifrey-teal" />
                  </div>
                </button>
              ))}
            </div>
          </div>
        </div>
      </section>
    );
  }

  if (currentStep === 'results' && result) {
    return (
      <section className="py-20 px-4 bg-gallifrey-white/50">
        <div className="container mx-auto max-w-4xl">
          {/* Results header */}
          <div className={`bg-gradient-to-r ${getRiskColor(result.level)} rounded-2xl p-8 mb-8 border-2`}>
