        font-size: 0.95rem;
        color: rgb(var(--gallifrey-medium-gray));
        margin-bottom: 0.25rem;
      }

      .contact-info p:last-child {
        font-weight: 600;
        color: rgb(var(--gallifrey-teal));
        font-size: 0.9rem;
        letter-spacing: 0.05em;
      }

      /* Spiritual Header */
      .spiritual-header {
        text-align: center;
        margin: 2rem 0;
        padding: 1.5rem;
        background: linear-gradient(135deg, rgb(var(--gallifrey-off-white)), rgba(var(--gallifrey-teal), 0.05));
        border-radius: 8px;
        border: 1px solid rgb(var(--gallifrey-border));
      }

      .spiritual-symbol {
        font-size: 1.5rem;
        margin-bottom: 0.5rem;
        font-weight: 600;
        color: rgb(var(--gallifrey-teal));
      }

      .generation-date {
        font-size: 0.95rem;
        color: rgb(var(--gallifrey-medium-gray));
        font-style: italic;
      }

      /* Document Content */
      .document-content {
        margin: 2rem 0;
        line-height: 1.7;
      }

      /* Typography */
      h1 {
        font-family: var(--font-heading);
        font-size: 2.25rem;
        font-weight: 600;
        color: rgb(var(--gallifrey-charcoal));
        margin: 2rem 0 1rem 0;
        letter-spacing: -0.02em;
        border-bottom: 2px solid rgb(var(--gallifrey-teal));
        padding-bottom: 0.5rem;
      }

      h2 {
        font-family: var(--font-heading);
        font-size: 1.8rem;
        font-weight: 600;
        color: rgb(var(--gallifrey-teal));
        margin: 1.8rem 0 1rem 0;
        letter-spacing: -0.01em;
      }

      h3 {
        font-family: var(--font-heading);
        font-size: 1.4rem;
        font-weight: 600;
        color: rgb(var(--gallifrey-charcoal));
        margin: 1.5rem 0 0.8rem 0;
      }

      h4 {
        font-family: var(--font-heading);
        font-size: 1.2rem;
        font-weight: 500;
        color: rgb(var(--gallifrey-dark-gray));
        margin: 1.2rem 0 0.6rem 0;
      }

      p {
        margin-bottom: 1rem;
        font-size: 1.05rem;
      }

      strong {
        color: rgb(var(--gallifrey-charcoal));
        font-weight: 600;
      }

      em {
        color: rgb(var(--gallifrey-teal));
        font-style: italic;
      }

      /* Lists */
      ul, ol {
        margin: 1rem 0;
        padding-left: 2rem;
      }

      li {
        margin-bottom: 0.5rem;
        font-size: 1.05rem;
      }

      /* Blockquotes */
      blockquote {
        border-left: 4px solid rgb(var(--gallifrey-teal));
        padding-left: 1.5rem;
        margin: 1.5rem 0;
        font-style: italic;
        color: rgb(var(--gallifrey-dark-gray));
        background: rgba(var(--gallifrey-teal), 0.03);
        padding: 1rem 1.5rem;
        border-radius: 0 8px 8px 0;
      }

      /* Special Sections */
      .dharmic-principle, .sacred-section {
        background: linear-gradient(135deg, rgb(var(--oyn-stone-50)), rgba(var(--oyn-orange-500), 0.05));
        border: 1px solid rgba(var(--oyn-orange-500), 0.2);
        border-radius: 8px;
        padding: 1.5rem;
        margin: 1.5rem 0;
      }

      .dharmic-principle h4, .sacred-section h4 {
        color: rgb(var(--oyn-orange-600));
        margin-top: 0;
      }

      /* Pricing Sections */
      .pricing-section {
        background: rgb(var(--gallifrey-off-white));
        border: 1px solid rgb(var(--gallifrey-border));
        border-radius: 8px;
        padding: 1.5rem;
        margin: 1rem 0;
      }

      .pricing-section h3 {
        color: rgb(var(--gallifrey-teal));
        margin-top: 0;
      }

      .price {
        font-size: 1.3rem;
        font-weight: 700;
        color: rgb(var(--gallifrey-charcoal));
        margin: 0.5rem 0;
      }

      /* Document Footer */
      .document-footer {
        margin-top: 3rem;
        border-top: 2px solid rgb(var(--gallifrey-border));
        padding-top: 1.5rem;
        font-size: 0.95rem;
        color: rgb(var(--gallifrey-medium-gray));
      }

      .footer-content {
        display: flex;
        justify-content: space-between;
        align-items: flex-end;
        flex-wrap: wrap;
        gap: 1rem;
      }

      .classification-section p {
        margin-bottom: 0.5rem;
      }

      .closing-blessing {
        text-align: right;
      }

      .closing-mantra {
        font-size: 1.1rem;
        color: rgb(var(--gallifrey-teal));
        font-style: italic;
        font-weight: 500;
      }

      /* Horizontal Rules */
      hr {
        border: none;
        height: 2px;
        background: linear-gradient(to right, transparent, rgb(var(--gallifrey-teal)), transparent);
        margin: 2rem 0;
      }

      /* Print Styles */
      @media print {
        body {
          max-width: none;
          padding: 0;
          font-size: 12pt;
          line-height: 1.5;
        }

        .document-header {
          break-after: avoid;
        }

        .spiritual-header {
          break-inside: avoid;
        }

        h1, h2, h3 {
          break-after: avoid;
        }

        .dharmic-principle, .sacred-section, .pricing-section {
          break-inside: avoid;
        }

        .document-footer {
          break-before: avoid;
        }

        /* Ensure consistent margins */
        @page {
          margin: 1in;
        }
      }

      /* Mobile Responsive */
      @media (max-width: 768px) {
        body {
          padding: 1rem;
        }

        .header-content {
          flex-direction: column;
          text-align: center;
        }

        .contact-section {
          text-align: center;
        }

        .company-name {
          font-size: 2rem;
        }

        .footer-content {
          flex-direction: column;
          text-align: center;
        }

        .closing-blessing {
          text-align: center;
        }
      }

