  /* Accessibility enhancements */
  .sr-only {
    position: absolute;
    width: 1px;
    height: 1px;
    padding: 0;
    margin: -1px;
    overflow: hidden;
    clip: rect(0, 0, 0, 0);
    white-space: nowrap;
    border: 0;
  }

  .focus\:not-sr-only:focus {
    position: static;
    width: auto;
    height: auto;
    padding: inherit;
    margin: inherit;
    overflow: visible;
    clip: auto;
    white-space: normal;
  }

  /* High contrast mode support */
  @media (prefers-contrast: high) {
    .btn {
      border: 2px solid currentColor;
    }

    .card {
      border: 2px solid currentColor;
    }
  }

  /* Reduced data mode optimizations */
  @media (prefers-reduced-data: reduce) {
    .animate-fade-up,
    .animate-scale-in,
    .animate-slide-left,
    .animate-float,
    .animate-shimmer {
      animation: none;
      transition: none;
    }

    .glass-card,
    .glass-card-dark {
      backdrop-filter: none;
      background: rgba(255, 255, 255, 0.95);
    }
  }
}

