/* ===== COIN ANIMATION STYLES ===== */
.coin-container {
  perspective: 1000px;
  display: flex;
  flex-direction: column;
  align-items: center;
  cursor: pointer;
}

.coin {
  width: 120px;
  height: 120px;
  position: relative;
  transform-style: preserve-3d;
  transition: transform 0.8s cubic-bezier(0.4, 0.0, 0.2, 1);
}

.coin.flipped {
  transform: rotateY(180deg);
}

.coin-face {
  position: absolute;
  width: 100%;
  height: 100%;
  border-radius: 50%;
  backface-visibility: hidden;
  display: flex;
  align-items: center;
  justify-content: center;
  background: linear-gradient(135deg, rgb(var(--gallifrey-teal)), rgb(var(--gallifrey-teal-dark)));
  box-shadow: 
    0 8px 32px rgba(var(--gallifrey-teal), 0.3),
    inset 0 2px 4px rgba(255, 255, 255, 0.2);
  border: 2px solid rgba(255, 255, 255, 0.2);
}

.coin-front {
  transform: rotateY(0deg);
}

.coin-back {
  transform: rotateY(180deg);
}

.coin-content {
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
}

.coin:hover {
  transform: translateY(-4px) rotateY(0deg);
  transition: all 0.3s ease-out;
}

.coin.flipped:hover {
  transform: translateY(-4px) rotateY(180deg);
}

@media (prefers-reduced-motion: reduce) {
  .coin {
    transition: none;
  }

  .coin:hover {
    transform: none;
  }
}

