import { useEffect, useRef, useState } from "react";

interface UseScrollAnimationOptions {
  threshold?: number;
  rootMargin?: string;
  triggerOnce?: boolean;
}

export function useScrollAnimation<T extends HTMLElement = HTMLElement>(
  options: UseScrollAnimationOptions = {}
) {
  const { threshold = 0.2, rootMargin = "0px", triggerOnce = true } = options;

  const ref = useRef<T>(null);
  const [isVisible, setIsVisible] = useState(false);
  const [hasAnimated, setHasAnimated] = useState(false);

  useEffect(() => {
    const element = ref.current;
    if (!element) return;

    // Check for reduced motion and reduced data preferences
    const prefersReducedMotion = window.matchMedia(
      "(prefers-reduced-motion: reduce)"
    ).matches;
    const prefersReducedData = window.matchMedia(
      "(prefers-reduced-data: reduce)"
    ).matches;

    if (prefersReducedMotion || prefersReducedData) {
      setIsVisible(true);
      setHasAnimated(true);
      return;
    }

    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setIsVisible(true);
          if (triggerOnce) {
            setHasAnimated(true);
            observer.unobserve(element);
          }
        } else if (!triggerOnce) {
          setIsVisible(false);
        }
      },
      {
        threshold,
        rootMargin,
      }
    );

    observer.observe(element);

    return () => {
      observer.unobserve(element);
    };
  }, [threshold, rootMargin, triggerOnce]);

  return { ref, isVisible, hasAnimated };
}

// Hook for staggered animations
export function useStaggeredAnimation<T extends HTMLElement = HTMLElement>(
  itemCount: number,
  options: UseScrollAnimationOptions = {}
) {
  const { ref, isVisible } = useScrollAnimation<T>(options);
  const [visibleItems, setVisibleItems] = useState<boolean[]>(
    new Array(itemCount).fill(false)
  );

  useEffect(() => {
    if (isVisible) {
      const timeouts: NodeJS.Timeout[] = [];

      for (let i = 0; i < itemCount; i++) {
        const timeout = setTimeout(() => {
          setVisibleItems((prev) => {
            const newState = [...prev];
            newState[i] = true;
            return newState;
          });
        }, i * 100); // 100ms stagger between items

        timeouts.push(timeout);
      }

      return () => {
        timeouts.forEach(clearTimeout);
      };
    }
  }, [isVisible, itemCount]);

  return { ref, visibleItems };
}

import { useState, useEffect } from 'react';
import { Eye, Shield } from 'lucide-react';

interface CoinAnimationProps {
  className?: string;
  autoFlip?: boolean;
  flipInterval?: number;
}

export function CoinAnimation({ 
  className = '', 
  autoFlip = true, 
  flipInterval = 4000 
}: CoinAnimationProps) {
  const [isFlipped, setIsFlipped] = useState(false);

  useEffect(() => {
    if (!autoFlip) return;

    const interval = setInterval(() => {
      setIsFlipped(prev => !prev);
    }, flipInterval);

    return () => clearInterval(interval);
  }, [autoFlip, flipInterval]);

  return (
    <div className={`coin-container ${className}`}>
      <div 
        className={`coin ${isFlipped ? 'flipped' : ''}`}
        onClick={() => setIsFlipped(!isFlipped)}
      >
        {/* Surveillance Side */}
        <div className="coin-face coin-front">
          <div className="coin-content">
            <Eye className="w-8 h-8 text-gallifrey-white" />
            <span className="text-xs font-medium text-gallifrey-white mt-2">
              Surveillance
            </span>
          </div>
        </div>

        {/* Privacy Side */}
        <div className="coin-face coin-back">
          <div className="coin-content">
            <Shield className="w-8 h-8 text-gallifrey-white" />
            <span className="text-xs font-medium text-gallifrey-white mt-2">
              Privacy
            </span>
          </div>
        </div>
      </div>

      {/* Subtle interaction hint */}
      <div className="text-center mt-4">
        <span className="text-xs text-gallifrey-charcoal/40 italic">
          Click to flip
        </span>
      </div>
    </div>
  );
}

