/* ===== COIN ANIMATION STYLES ===== */
  .coin-container {
    perspective: 1000px;
    display: flex;
    flex-direction: column;
    align-items: center;
    cursor: pointer;
  }
  .coin {
    width: 120px;
    height: 120px;
    position: relative;
    transform-style: preserve-3d;
    transition: transform 0.8s cubic-bezier(0.4, 0.0, 0.2, 1);
