      inset 0 2px 4px rgba(255, 255, 255, 0.2);
    border: 2px solid rgba(255, 255, 255, 0.2);
  }

  .coin-front {
    transform: rotateY(0deg);
  }

  .coin-back {
    transform: rotateY(180deg);
  }

  .coin-content {
    display: flex;
    flex-direction: column;
    align-items: center;
    text-align: center;
  }

  .coin:hover {
    transform: translateY(-4px) rotateY(0deg);
    transition: all 0.3s ease-out;
  }

  .coin.flipped:hover {
    transform: translateY(-4px) rotateY(180deg);
  }

  @media (prefers-reduced-motion: reduce) {
    .coin {
      transition: none;
    }

    .coin:hover {
      transform: none;
    }
  }

  .campaign-cta {
    min-height: 48px;
    padding: 16px 24px;
  }

  @media (max-width: 768px) {
    .campaign-cta {
      min-height: 52px;
      padding: 18px 28px;
      font-size: 1.1rem;
    }
  }

  /* Accessibility enhancements */
  .sr-only {
    position: absolute;
    width: 1px;
    height: 1px;
    padding: 0;
    margin: -1px;
    overflow: hidden;
    clip: rect(0, 0, 0, 0);
    white-space: nowrap;
    border: 0;
  }

  .focus\:not-sr-only:focus {
    position: static;
    width: auto;
    height: auto;
    padding: inherit;
    margin: inherit;
    overflow: visible;
    clip: auto;
    white-space: normal;
  }

  /* High contrast mode support */
  @media (prefers-contrast: high) {
    .btn {
      border: 2px solid currentColor;
    }

    .card {
      border: 2px solid currentColor;
    }
  }

  /* Reduced data mode optimizations */
  @media (prefers-reduced-data: reduce) {
    .animate-fade-up,
    .animate-scale-in,
    .animate-slide-left,
    .animate-float,
    .animate-shimmer {
      animation: none;
      transition: none;
    }

    .glass-card,
    .glass-card-dark {
      backdrop-filter: none;
      background: rgba(255, 255, 255, 0.95);
    }
  }
}

