import React, { useEffect, useRef } from 'react';

interface WorkOSBackgroundProps {
  className?: string;
}

export const WorkOSBackground: React.FC<WorkOSBackgroundProps> = ({ className = '' }) => {
  const canvasRef = useRef<HTMLCanvasElement>(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    const resizeCanvas = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
    };

    resizeCanvas();
    window.addEventListener('resize', resizeCanvas);

    // Circuit-like nodes and connections
    const nodes: Array<{ x: number; y: number; pulse: number; connections: number[] }> = [];
    const nodeCount = 12;

    // Initialize nodes
    for (let i = 0; i < nodeCount; i++) {
      nodes.push({
        x: Math.random() * canvas.width,
        y: Math.random() * canvas.height,
        pulse: Math.random() * Math.PI * 2,
        connections: []
      });
    }

    // Create connections between nearby nodes
    nodes.forEach((node, i) => {
      nodes.forEach((otherNode, j) => {
        if (i !== j) {
          const distance = Math.sqrt(
            Math.pow(node.x - otherNode.x, 2) + Math.pow(node.y - otherNode.y, 2)
          );
          if (distance < 200 && node.connections.length < 3) {
            node.connections.push(j);
          }
        }
      });
    });

    let animationFrame: number;
    let time = 0;

    const animate = () => {
      time += 0.01;

      // Clear canvas with gradient background
      const gradient = ctx.createLinearGradient(0, 0, canvas.width, canvas.height);
      gradient.addColorStop(0, '#f8fafc'); // Very light gray
      gradient.addColorStop(0.3, '#f1f5f9'); // Light gray
      gradient.addColorStop(0.7, '#e2e8f0'); // Slightly darker
      gradient.addColorStop(1, '#cbd5e1'); // Darker gray

      ctx.fillStyle = gradient;
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      // Draw connections
      nodes.forEach((node, i) => {
        node.connections.forEach(connectionIndex => {
          const connectedNode = nodes[connectionIndex];
          if (connectedNode) {
            const opacity = 0.1 + 0.05 * Math.sin(time + i * 0.5);

            ctx.strokeStyle = `rgba(59, 130, 246, ${opacity})`; // Blue color
            ctx.lineWidth = 1;
            ctx.beginPath();
            ctx.moveTo(node.x, node.y);
            ctx.lineTo(connectedNode.x, connectedNode.y);
            ctx.stroke();
          }
        });
      });

      // Draw nodes
      nodes.forEach((node, i) => {
        // Update pulse
        node.pulse += 0.02;

        const pulseSize = 2 + Math.sin(node.pulse) * 1;
        const opacity = 0.3 + 0.2 * Math.sin(node.pulse);

        // Outer glow
        const glowGradient = ctx.createRadialGradient(
          node.x, node.y, 0,
          node.x, node.y, pulseSize * 3
        );
        glowGradient.addColorStop(0, `rgba(59, 130, 246, ${opacity * 0.3})`);
        glowGradient.addColorStop(1, 'rgba(59, 130, 246, 0)');

        ctx.fillStyle = glowGradient;
        ctx.beginPath();
        ctx.arc(node.x, node.y, pulseSize * 3, 0, Math.PI * 2);
        ctx.fill();

        // Core node
        ctx.fillStyle = `rgba(59, 130, 246, ${opacity})`;
        ctx.beginPath();
        ctx.arc(node.x, node.y, pulseSize, 0, Math.PI * 2);
        ctx.fill();
      });

      // Floating particles
      const particleCount = 8;
      for (let i = 0; i < particleCount; i++) {
        const x = (Math.sin(time * 0.5 + i) * 100) + canvas.width * 0.5;
        const y = (Math.cos(time * 0.3 + i * 0.7) * 80) + canvas.height * 0.5;
        const size = 1 + Math.sin(time * 2 + i) * 0.5;
        const opacity = 0.1 + 0.1 * Math.sin(time * 3 + i);

        ctx.fillStyle = `rgba(168, 85, 247, ${opacity})`; // Purple color
        ctx.beginPath();
        ctx.arc(x, y, size, 0, Math.PI * 2);
        ctx.fill();
      }

      animationFrame = requestAnimationFrame(animate);
    };

    animate();

    return () => {
      window.removeEventListener('resize', resizeCanvas);
      cancelAnimationFrame(animationFrame);
    };
  }, []);

  return (
    <canvas
      ref={canvasRef}
      className={`absolute inset-0 w-full h-full pointer-events-none ${className}`}
      style={{ zIndex: 1 }}
      aria-hidden="true"
    />
  );
};

import React, { useEffect, useState } from 'react';

interface GradientTextProps {
  children: React.ReactNode;
  className?: string;
  animate?: boolean;
  speed?: 'slow' | 'medium' | 'fast';
  colors?: {
    from: string;
    via?: string;
    to: string;
  };
}

export const GradientText: React.FC<GradientTextProps> = ({
  children,
  className = '',
  animate = true,
  speed = 'medium',
  colors = {
    from: 'from-purple-500',
    via: 'via-blue-500',
    to: 'to-teal-400'
  }
}) => {
  const [animationClass, setAnimationClass] = useState('');

  useEffect(() => {
    if (!animate) return;

    const speedMap = {
      slow: 'animate-gradient-slow',
      medium: 'animate-gradient-medium',
      fast: 'animate-gradient-fast'
    };

    setAnimationClass(speedMap[speed]);
  }, [animate, speed]);

  const gradientClasses = `
    bg-gradient-to-r ${colors.from} ${colors.via || ''} ${colors.to}
    bg-clip-text text-transparent font-bold
    ${animate ? 'bg-size-200 ' + animationClass : ''}
    ${className}
  `.trim();

  return (
    <span 
      className={gradientClasses}
      style={{
        WebkitBackgroundClip: 'text',
        WebkitTextFillColor: 'transparent',
        backgroundClip: 'text',
        ...(animate && {
          backgroundSize: '200% 200%',
          animation: speed === 'slow' ? 'gradient-x 8s ease infinite' :
                    speed === 'medium' ? 'gradient-x 5s ease infinite' :
                    'gradient-x 3s ease infinite'
        })
      }}
    >
      {children}
    </span>
  );
};

// Animated gradient text with typing effect
interface AnimatedGradientTextProps extends Omit<GradientTextProps, 'children'> {
  text: string;
  typingSpeed?: number;
  startDelay?: number;
}

export const AnimatedGradientText: React.FC<AnimatedGradientTextProps> = ({
  text,
  typingSpeed = 100,
  startDelay = 0,
  ...gradientProps
}) => {
  const [displayText, setDisplayText] = useState('');
  const [isTyping, setIsTyping] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => {
      setIsTyping(true);
      let currentIndex = 0;

      const typingInterval = setInterval(() => {
        setDisplayText(text.slice(0, currentIndex + 1));
        currentIndex++;

        if (currentIndex >= text.length) {
          clearInterval(typingInterval);
          setIsTyping(false);
        }
      }, typingSpeed);

      return () => clearInterval(typingInterval);
    }, startDelay);

    return () => clearTimeout(timer);
  }, [text, typingSpeed, startDelay]);

  return (
    <GradientText {...gradientProps}>
      {displayText}
      {isTyping && (
        <span className="animate-pulse text-current">|</span>
      )}
    </GradientText>
  );
};

// Multiple gradient text carousel
interface GradientTextCarouselProps extends Omit<GradientTextProps, 'children'> {
  texts: string[];
  interval?: number;
  transitionDuration?: number;
}

export const GradientTextCarousel: React.FC<GradientTextCarouselProps> = ({
  texts,
  interval = 3000,
  transitionDuration = 500,
  ...gradientProps
}) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isVisible, setIsVisible] = useState(true);

  useEffect(() => {
    if (texts.length <= 1) return;

    const cycleText = () => {
      setIsVisible(false);

      setTimeout(() => {
        setCurrentIndex((prev) => (prev + 1) % texts.length);
        setIsVisible(true);
      }, transitionDuration / 2);
    };

    const intervalId = setInterval(cycleText, interval);
    return () => clearInterval(intervalId);
  }, [texts.length, interval, transitionDuration]);

  return (
    <div
      className={`transition-opacity duration-${transitionDuration / 2} ${
        isVisible ? 'opacity-100' : 'opacity-0'
      }`}
    >
      <GradientText {...gradientProps}>
        {texts[currentIndex] || texts[0]}
      </GradientText>
    </div>
  );
};

import React, { useState, useEffect } from 'react';
import { Copy, Check, Terminal } from 'lucide-react';

interface CodeSnippetProps {
  code: string;
  language?: string;
  title?: string;
  className?: string;
  showLineNumbers?: boolean;
  animated?: boolean;
  animationDelay?: number;
}

export const CodeSnippet: React.FC<CodeSnippetProps> = ({
  code,
  language = 'bash',
  title,
  className = '',
  showLineNumbers = true,
  animated = false,
  animationDelay = 0
}) => {
  const [copied, setCopied] = useState(false);
  const [displayedCode, setDisplayedCode] = useState(animated ? '' : code);
  const [isTyping, setIsTyping] = useState(false);

  const lines = code.split('\n');
  const displayedLines = displayedCode.split('\n');

  useEffect(() => {
    if (!animated) return;

    const timer = setTimeout(() => {
      setIsTyping(true);
      let currentIndex = 0;

      const typingInterval = setInterval(() => {
        setDisplayedCode(code.slice(0, currentIndex + 1));
        currentIndex++;

        if (currentIndex >= code.length) {
          clearInterval(typingInterval);
          setIsTyping(false);
        }
      }, 30);

      return () => clearInterval(typingInterval);
    }, animationDelay);

    return () => clearTimeout(timer);
  }, [code, animated, animationDelay]);

  const handleCopy = async () => {
    try {
      await navigator.clipboard.writeText(code);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (err) {
      console.error('Failed to copy code:', err);
    }
  };

  return (
    <div className={`relative group ${className}`}>
      {/* Header */}
      <div className="flex items-center justify-between px-4 py-3 bg-gray-800 rounded-t-lg border-b border-gray-700">
        <div className="flex items-center gap-2">
          <Terminal className="w-4 h-4 text-gray-400" />
          <span className="text-sm font-medium text-gray-300">
            {title || `${language}.sh`}
          </span>
        </div>
        <button
          onClick={handleCopy}
          className="flex items-center gap-1.5 px-2 py-1 text-xs text-gray-400 hover:text-white bg-gray-700 hover:bg-gray-600 rounded transition-colors"
          aria-label="Copy code"
        >
          {copied ? (
            <>
              <Check className="w-3 h-3" />
              Copied!
            </>
          ) : (
            <>
              <Copy className="w-3 h-3" />
              Copy
            </>
          )}
        </button>
      </div>

      {/* Code Content */}
      <div className="relative bg-gray-900 rounded-b-lg overflow-hidden">
        <pre className="p-4 overflow-x-auto text-sm">
          <code className="text-gray-100 font-mono">
            {displayedLines.map((line, index) => (
              <div key={index} className="flex items-start">
                {showLineNumbers && (
                  <span className="mr-4 text-gray-500 select-none text-xs w-8 flex-shrink-0 text-right">
                    {index + 1}
                  </span>
                )}
                <span className="flex-1">
                  <SyntaxHighlight line={line} language={language} />
                  {animated && isTyping && index === displayedLines.length - 1 && (
                    <span className="animate-pulse text-green-400">_</span>
                  )}
                </span>
              </div>
            ))}
          </code>
        </pre>

        {/* Gradient overlay for visual appeal */}
        <div className="absolute inset-0 bg-gradient-to-r from-transparent via-transparent to-blue-500/5 pointer-events-none" />
      </div>
    </div>
  );
};

// Simple syntax highlighting component
interface SyntaxHighlightProps {
  line: string;
  language: string;
}

const SyntaxHighlight: React.FC<SyntaxHighlightProps> = ({ line, language }) => {
  if (language === 'bash' || language === 'shell') {
    // Basic bash syntax highlighting
    if (line.startsWith('#')) {
      return <span className="text-gray-500">{line}</span>;
    }
    if (line.includes('sudo') || line.includes('docker') || line.includes('kubectl')) {
      const parts = line.split(' ');
      return (
        <>
          {parts.map((part, index) => {
            if (part === 'sudo' || part === 'docker' || part === 'kubectl') {
              return <span key={index} className="text-purple-400">{part} </span>;
            }
            if (part.startsWith('--')) {
              return <span key={index} className="text-yellow-400">{part} </span>;
            }
            if (part.startsWith('-')) {
              return <span key={index} className="text-blue-400">{part} </span>;
            }
            return <span key={index}>{part} </span>;
          })}
        </>
      );
    }
  }

  if (language === 'yaml' || language === 'yml') {
    if (line.includes(':')) {
      const [key, ...values] = line.split(':');
      return (
        <>
          <span className="text-blue-400">{key}:</span>
          <span>{values.join(':')}</span>
        </>
      );
    }
  }

  return <span>{line}</span>;
};

