/* Reduced data mode optimizations */
@media (prefers-reduced-data: reduce) {
  .animate-fade-up,
  .animate-scale-in,
  .animate-slide-left,
  .animate-float,
  .animate-shimmer {
    animation: none;
    transition: none;
  }

  .glass-card,
  .glass-card-dark {
    backdrop-filter: none;
    background: rgba(255, 255, 255, 0.95);
  }
}

/* ===== ENHANCED NAVBAR STYLES ===== */

/* Professional navbar with security-first styling */
.navbar-enhanced {
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(12px);
  border-bottom: 1px solid rgba(var(--gallifrey-border), 0.3);
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.navbar-enhanced.scrolled {
  background: rgba(255, 255, 255, 0.98);
  box-shadow: 0 4px 20px rgba(var(--gallifrey-charcoal), 0.08);
  backdrop-filter: blur(16px);
}

/* Enhanced navigation items */
.nav-item-enhanced {
  position: relative;
  padding: 0.5rem 1rem;
  border-radius: 0.5rem;
  transition: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);
  color: rgb(var(--gallifrey-medium-gray));
  font-weight: 500;
  font-size: 0.875rem;
}

.nav-item-enhanced:hover {
  color: rgb(var(--gallifrey-teal));
  background-color: rgba(var(--gallifrey-teal), 0.05);
  transform: translateY(-1px);
}

.nav-item-enhanced.active {
  color: rgb(var(--gallifrey-teal));
  background-color: rgba(var(--gallifrey-teal), 0.1);
  box-shadow: 0 2px 8px rgba(var(--gallifrey-teal), 0.15);
}

.nav-item-enhanced.active::after {
  content: '';
  position: absolute;
  bottom: -2px;
  left: 50%;
  transform: translateX(-50%);
  width: 6px;
  height: 6px;
  background-color: rgb(var(--gallifrey-teal));
  border-radius: 50%;
  animation: navIndicatorPulse 2s ease-in-out infinite;
}

/* Enhanced mobile menu */
.mobile-menu-enhanced {
  background: rgba(255, 255, 255, 0.98);
  backdrop-filter: blur(16px);
  border-top: 1px solid rgba(var(--gallifrey-border), 0.3);
  border-radius: 0 0 1rem 1rem;
  box-shadow: 0 8px 32px rgba(var(--gallifrey-charcoal), 0.12);
}

.mobile-nav-item {
  padding: 1rem 1.5rem;
  border-radius: 0.75rem;
  margin: 0.25rem 0;
  transition: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);
  border: 1px solid transparent;
}

.mobile-nav-item:hover,
.mobile-nav-item.active {
  background-color: rgba(var(--gallifrey-teal), 0.08);
  border-color: rgba(var(--gallifrey-teal), 0.15);
  color: rgb(var(--gallifrey-teal));
}

.mobile-nav-item.active {
  box-shadow: 0 2px 12px rgba(var(--gallifrey-teal), 0.15);
}

/* Enhanced logo styling */
.logo-enhanced {
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  filter: drop-shadow(0 2px 4px rgba(var(--gallifrey-charcoal), 0.1));
}

.logo-enhanced:hover {
  transform: scale(1.02);
  filter: drop-shadow(0 4px 8px rgba(var(--gallifrey-teal), 0.2));
}

/* Enhanced CTA button */
.cta-enhanced {
  background: linear-gradient(135deg, rgb(var(--gallifrey-teal)), rgb(var(--gallifrey-teal-dark)));
  box-shadow: 0 4px 12px rgba(var(--gallifrey-teal), 0.3);
  transition: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);
  position: relative;
  overflow: hidden;
}

.cta-enhanced::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent);
  transition: left 0.6s ease;
}

.cta-enhanced:hover::before {
  left: 100%;
}

.cta-enhanced:hover {
  transform: translateY(-2px);
  box-shadow: 0 8px 24px rgba(var(--gallifrey-teal), 0.4);
}

/* Security-themed visual enhancements */
.security-indicator {
  position: relative;
  display: inline-flex;
  align-items: center;
  gap: 0.5rem;
}

.security-indicator::before {
  content: '';
  width: 8px;
  height: 8px;
  background-color: #10b981;
  border-radius: 50%;
  box-shadow: 0 0 8px rgba(16, 185, 129, 0.6);
  animation: securityPulse 2s ease-in-out infinite;
}

/* Keyboard navigation enhancements */
.nav-item-enhanced:focus {
  outline: 2px solid rgb(var(--gallifrey-teal));
  outline-offset: 2px;
  background-color: rgba(var(--gallifrey-teal), 0.1);
}

.mobile-nav-item:focus {
  outline: 2px solid rgb(var(--gallifrey-teal));
  outline-offset: 2px;
}

/* Smooth mobile menu transitions */
.mobile-menu-slide-enter {
  max-height: 0;
  opacity: 0;
  transform: translateY(-10px);
}

.mobile-menu-slide-enter-active {
  max-height: 500px;
  opacity: 1;
  transform: translateY(0);
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.mobile-menu-slide-exit {
  max-height: 500px;
  opacity: 1;
  transform: translateY(0);
}

.mobile-menu-slide-exit-active {
  max-height: 0;
  opacity: 0;
  transform: translateY(-10px);
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

/* Keyframe animations for navbar */
@keyframes navIndicatorPulse {
  0%, 100% {
    opacity: 1;
    transform: translateX(-50%) scale(1);
  }
  50% {
    opacity: 0.6;
    transform: translateX(-50%) scale(1.2);
  }
}

@keyframes securityStatusPulse {
  0%, 100% {
    opacity: 0.8;
    box-shadow: 0 0 8px rgba(16, 185, 129, 0.6);
  }
  50% {
    opacity: 1;
    box-shadow: 0 0 16px rgba(16, 185, 129, 0.8);
  }
}

